// @ts-nocheck
import React, { createContext, useState, useContext, useEffect } from 'react';
import { useGLTF } from '@react-three/drei';
import { getStadiumModelPath } from './modelLoader';
import { ColladaLoader } from 'three/examples/jsm/loaders/ColladaLoader';
import { useLoader } from '@react-three/fiber';

// Register the ColladaLoader with useLoader
useLoader.preload(ColladaLoader, '/models/stadium.dae');

// Create a context for sharing model loading state
const ModelContext = createContext({
  model: null,
  isLoading: true,
  error: null,
});

// Provider component that will wrap the application
export const ModelProvider = ({ children, customModelUrl = null }) => {
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [model, setModel] = useState(null);

  // Helper function to check if a model is valid and has usable geometry
  const isValidModel = (loadedModel) => {
    if (!loadedModel || !loadedModel.scene) {
      console.log('[ModelContext] Model is invalid: No scene found');
      return false;
    }

    // Check if scene has any meaningful children
    const hasChildren = loadedModel.scene.children && loadedModel.scene.children.length > 0;
    if (!hasChildren) {
      console.log('[ModelContext] Model is invalid: Scene has no children');
      return false;
    }

    // Check if any child has geometry (mesh or nested children)
    const hasMeaningfulContent = loadedModel.scene.children.some(child => {
      // Check if it's a mesh with geometry
      if (child.type === 'Mesh' && child.geometry) {
        return true;
      }

      // Check if it's a group with children
      if ((child.type === 'Group' || child.type === 'Object3D') && 
          child.children && child.children.length > 0) {
        return true;
      }

      return false;
    });

    if (!hasMeaningfulContent) {
      console.log('[ModelContext] Model is invalid: No meaningful geometry found');
      return false;
    }

    console.log('[ModelContext] Model is valid and has usable geometry');
    return true;
  };

  // Load the model once when the provider mounts or when customModelUrl changes
  useEffect(() => {
    setIsLoading(true);
    setError(null);

    // Determine which model to load
    const modelPath = customModelUrl || getStadiumModelPath();
    console.log('[ModelContext] Loading model from path:', modelPath);

    const loadModel = async () => {
      try {
        // If we have a custom model URL, just load it directly
        if (customModelUrl) {
          console.log('[ModelContext] Loading custom model from URL:', customModelUrl);

          // Preload the model
          useGLTF.preload(customModelUrl);
          console.log('[ModelContext] Custom model preloaded successfully');

          // Load the model
          const loadedModel = useGLTF(customModelUrl);
          console.log('[ModelContext] Custom model loaded successfully:', loadedModel);

          // Check if the model has a scene and is valid
          if (loadedModel.scene) {
            console.log('[ModelContext] Custom model has a valid scene:', loadedModel.scene);
            console.log('[ModelContext] Scene children:', loadedModel.scene.children.length);

            // Log information about each child in the scene
            loadedModel.scene.children.forEach((child, index) => {
              console.log(`[ModelContext] Scene child ${index}:`, child.type, child.name);
            });

            // Validate the model
            const isValid = isValidModel(loadedModel);
            if (!isValid) {
              console.warn('[ModelContext] Custom model is not valid for rendering, will use fallback');
              setError(new Error('Custom model is not valid for rendering'));
            }
          } else {
            console.warn('[ModelContext] Custom model loaded but has no scene');
            setError(new Error('Custom model has no scene'));
          }

          setModel(loadedModel);
          setIsLoading(false);
          return;
        }

        // Otherwise, fetch and check the default model
        const response = await fetch(modelPath);
        console.log('[ModelContext] Fetch response status:', response.status);
        if (!response.ok) {
          throw new Error(`Failed to fetch model: ${response.status} ${response.statusText}`);
        }
        console.log('[ModelContext] Model file exists, proceeding with loading');

        // Log the content type and size
        console.log('[ModelContext] Content-Type:', response.headers.get('content-type'));
        console.log('[ModelContext] Content-Length:', response.headers.get('content-length'));

        const blob = await response.blob();
        console.log('[ModelContext] Model blob size:', blob.size, 'bytes');

        // Create a local URL for the blob - this helps with debugging
        const blobUrl = URL.createObjectURL(blob);
        console.log('[ModelContext] Created blob URL:', blobUrl);

        // Load and preload the model using useGLTF
        console.log('[ModelContext] Preloading model...');
        useGLTF.preload(modelPath);
        console.log('[ModelContext] Model preloaded successfully');

        // Load the model - we're using the original modelPath here, not the blob URL
        // because useGLTF has better caching with the original path
        console.log('[ModelContext] Loading model...');
        const loadedModel = useGLTF(modelPath);
        console.log('[ModelContext] Model loaded successfully:', loadedModel);

        // Check if the model has a scene
        if (loadedModel.scene) {
          console.log('[ModelContext] Model has a valid scene:', loadedModel.scene);
          console.log('[ModelContext] Scene children:', loadedModel.scene.children.length);

          // Log information about each child in the scene
          loadedModel.scene.children.forEach((child, index) => {
            console.log(`[ModelContext] Scene child ${index}:`, child.type, child.name);
          });

          // Validate the model
          const isValid = isValidModel(loadedModel);
          if (!isValid) {
            console.warn('[ModelContext] Model is not valid for rendering, will use fallback');
            setError(new Error('Model is not valid for rendering'));
          }
        } else {
          console.warn('[ModelContext] Model loaded but has no scene');
          setError(new Error('Model has no scene'));
        }

        setModel(loadedModel);

        // Cleanup the blob URL
        URL.revokeObjectURL(blobUrl);
      } catch (err) {
        console.error('[ModelContext] Error during model loading process:', err);
        setError(err);
      } finally {
        setIsLoading(false);
      }
    };

    loadModel();

    // Cleanup function
    return () => {
      try {
        if (customModelUrl) {
          useGLTF.dispose(customModelUrl);
        } else {
          useGLTF.dispose(modelPath);
        }
      } catch (err) {
        console.error('[ModelContext] Error disposing model:', err);
      }
    };
  }, [customModelUrl]);

  // Debug output when model or error changes
  useEffect(() => {
    if (model) {
      console.log('[ModelContext] Model is available in context:', model);
    }
    if (error) {
      console.error('[ModelContext] Error in model context:', error);
    }
  }, [model, error]);

  // Value to be provided to consumers
  const value = {
    model,
    isLoading,
    error,
    isCustomModel: !!customModelUrl
  };

  return (
    <ModelContext.Provider value={value}>
      {children}
    </ModelContext.Provider>
  );
};

// Custom hook for consuming the context
export const useModel = () => {
  const context = useContext(ModelContext);
  if (!context) {
    throw new Error('useModel must be used within a ModelProvider');
  }
  return context;
};

export default ModelContext;
