// @ts-nocheck
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import MiniSeatView from './MiniSeatView';

const TicketForm = ({ selectedSeat, onViewToggle, onPurchase, viewingMode, modelLoaded = true }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    paymentMethod: 'credit'
  });

  const [errors, setErrors] = useState({});
  const [expandView, setExpandView] = useState(false);

  // Reset expandView state when viewingMode changes
  useEffect(() => {
    if (!viewingMode) {
      setExpandView(false);
    }
  }, [viewingMode]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when field is edited
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Phone number is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      onPurchase();
    }
  };

  const handleExpandView = () => {
    setExpandView(true);
    onViewToggle();
  };

  if (!selectedSeat) {
    return (
      <FormContainer>
        <Title>Ticket Information</Title>
        <EmptyState>
          <EmptyStateText>Please select a seat from the stadium map to continue.</EmptyStateText>
        </EmptyState>
      </FormContainer>
    );
  }

  return (
    <FormContainer>
      <Title>Ticket Information</Title>

      <SeatInfo>
        <SeatInfoItem>
          <Label>Section:</Label>
          <Value>{selectedSeat.section}</Value>
        </SeatInfoItem>
        <SeatInfoItem>
          <Label>Row:</Label>
          <Value>{selectedSeat.row}</Value>
        </SeatInfoItem>
        <SeatInfoItem>
          <Label>Seat:</Label>
          <Value>{selectedSeat.number}</Value>
        </SeatInfoItem>
        <SeatInfoItem>
          <Label>Price:</Label>
          <Value>${selectedSeat.price}</Value>
        </SeatInfoItem>
      </SeatInfo>

      {/* Mini 3D View */}
      {selectedSeat && modelLoaded && !viewingMode && (
        <MiniViewContainer>
          <MiniViewHeader>
            <MiniViewTitle>Preview from this seat</MiniViewTitle>
            <ExpandButton onClick={handleExpandView}>
              <ExpandIcon>â¤¢</ExpandIcon>
            </ExpandButton>
          </MiniViewHeader>
          <MiniSeatView 
            seatCoordinates={selectedSeat.coordinates}
            seat={selectedSeat}
            mini={true}
          />
        </MiniViewContainer>
      )}

      {viewingMode && (
        <ViewingModeMessage>
          <p>Viewing expanded 3D preview. Click the X in the top-right corner to return to the map view.</p>
        </ViewingModeMessage>
      )}

      {!modelLoaded && (
        <ModelLoadingNote>
          3D model is still loading. Please wait a moment before previewing the seat view.
        </ModelLoadingNote>
      )}

      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <FormLabel htmlFor="name">Full Name</FormLabel>
          <FormInput
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            error={errors.name}
          />
          {errors.name && <ErrorMessage>{errors.name}</ErrorMessage>}
        </FormGroup>

        <FormGroup>
          <FormLabel htmlFor="email">Email Address</FormLabel>
          <FormInput
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            error={errors.email}
          />
          {errors.email && <ErrorMessage>{errors.email}</ErrorMessage>}
        </FormGroup>

        <FormGroup>
          <FormLabel htmlFor="phone">Phone Number</FormLabel>
          <FormInput
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            error={errors.phone}
          />
          {errors.phone && <ErrorMessage>{errors.phone}</ErrorMessage>}
        </FormGroup>

        <FormGroup>
          <FormLabel htmlFor="paymentMethod">Payment Method</FormLabel>
          <FormSelect
            id="paymentMethod"
            name="paymentMethod"
            value={formData.paymentMethod}
            onChange={handleChange}
          >
            <option value="credit">Credit Card</option>
            <option value="paypal">PayPal</option>
            <option value="apple">Apple Pay</option>
          </FormSelect>
        </FormGroup>

        <SubmitButton type="submit">Purchase Ticket</SubmitButton>
      </Form>
    </FormContainer>
  );
};

const FormContainer = styled.div`
  height: 100%;
  display: flex;
  flex-direction: column;
`;

const Title = styled.h2`
  margin-bottom: 20px;
  color: #333;
`;

const EmptyState = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #f9f9f9;
  border-radius: 8px;
  padding: 30px;
`;

const EmptyStateText = styled.p`
  text-align: center;
  color: #666;
  font-size: 16px;
`;

const SeatInfo = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
