// @ts-nocheck
import React, { createContext, useState, useContext, useEffect } from 'react';
import { useGLTF } from '@react-three/drei';
import { getStadiumModelPath } from './modelLoader';

// Create a context for sharing model loading state
const ModelContext = createContext({
  model: null,
  isLoading: true,
  error: null,
});

// Provider component that will wrap the application
export const ModelProvider = ({ children }) => {
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [model, setModel] = useState(null);

  // Load the model once when the provider mounts
  useEffect(() => {
    const modelPath = getStadiumModelPath();
    console.log('[ModelContext] Loading model from path:', modelPath);

    // Check if the model file exists
    fetch(modelPath)
      .then(response => {
        console.log('[ModelContext] Fetch response status:', response.status);
        if (!response.ok) {
          throw new Error(`Failed to fetch model: ${response.status} ${response.statusText}`);
        }
        console.log('[ModelContext] Model file exists, proceeding with loading');

        // Log the content type and size
        console.log('[ModelContext] Content-Type:', response.headers.get('content-type'));
        console.log('[ModelContext] Content-Length:', response.headers.get('content-length'));

        return response.blob();
      })
      .then(blob => {
        console.log('[ModelContext] Model blob size:', blob.size, 'bytes');

        try {
          // Create a local URL for the blob - this helps with debugging
          const blobUrl = URL.createObjectURL(blob);
          console.log('[ModelContext] Created blob URL:', blobUrl);

          // Load and preload the model using useGLTF
          console.log('[ModelContext] Preloading model...');
          useGLTF.preload(modelPath);
          console.log('[ModelContext] Model preloaded successfully');

          // Load the model - we're using the original modelPath here, not the blob URL
          // because useGLTF has better caching with the original path
          console.log('[ModelContext] Loading model...');
          const loadedModel = useGLTF(modelPath);
          console.log('[ModelContext] Model loaded successfully:', loadedModel);

          // Check if the model has a scene
          if (loadedModel.scene) {
            console.log('[ModelContext] Model has a valid scene:', loadedModel.scene);
            console.log('[ModelContext] Scene children:', loadedModel.scene.children.length);

            // Log information about each child in the scene
            loadedModel.scene.children.forEach((child, index) => {
              console.log(`[ModelContext] Scene child ${index}:`, child.type, child.name);
            });
          } else {
            console.warn('[ModelContext] Model loaded but has no scene');
          }

          setModel(loadedModel);
          setIsLoading(false);

          // Cleanup the blob URL
          URL.revokeObjectURL(blobUrl);
        } catch (err) {
          console.error('[ModelContext] Error during model loading process:', err);
          setError(err);
          setIsLoading(false);
        }
      })
      .catch(err => {
        console.error('[ModelContext] Error fetching model:', err);
        setError(err);
        setIsLoading(false);
      });

    // Cleanup function
    return () => {
      try {
        useGLTF.dispose(modelPath);
      } catch (err) {
        console.error('[ModelContext] Error disposing model:', err);
      }
    };
  }, []);

  // Debug output when model or error changes
  useEffect(() => {
    if (model) {
      console.log('[ModelContext] Model is available in context:', model);
    }
    if (error) {
      console.error('[ModelContext] Error in model context:', error);
    }
  }, [model, error]);

  // Value to be provided to consumers
  const value = {
    model,
    isLoading,
    error,
  };

  return (
    <ModelContext.Provider value={value}>
      {children}
    </ModelContext.Provider>
  );
};

// Custom hook for consuming the context
export const useModel = () => {
  const context = useContext(ModelContext);
  if (!context) {
    throw new Error('useModel must be used within a ModelProvider');
  }
  return context;
};

export default ModelContext;
