// @ts-nocheck
import React, { useState, useEffect, Suspense, useRef, useMemo, useCallback, memo } from 'react';
import styled from 'styled-components';
import { Canvas, useThree } from '@react-three/fiber';
import { OrbitControls, Html, Text } from '@react-three/drei';
import { useModel } from '../utils/ModelContext';
import * as THREE from 'three';

// Background image component - replaces 3D model with a 2D image
const ArenaBackground = () => {
  // Get access to the Three.js renderer size
  const { viewport } = useThree();

  return (
    <group>
      {/* The background image is now a standalone div in the HTML overlay */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backgroundImage: `url(${process.env.PUBLIC_URL}/arena_outline.png)`,
        backgroundSize: 'contain',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        zIndex: -1,
        pointerEvents: 'none'
      }} />
    </group>
  );
};

// Find the coordinate bounds to calculate scaling factors
const calcScalingFactors = (coordinates) => {
  if (!coordinates || coordinates.length === 0) {
    return { xScale: 1, zScale: 1, xOffset: 0, zOffset: 0 };
  }

  // Find the min/max of all coordinates
  let minX = Infinity;
  let maxX = -Infinity;
  let minZ = Infinity;
  let maxZ = -Infinity;

  coordinates.forEach(coord => {
    minX = Math.min(minX, coord.x);
    maxX = Math.max(maxX, coord.x);
    minZ = Math.min(minZ, coord.z);
    maxZ = Math.max(maxZ, coord.z);
  });

  // Calculate width and height of the data
  const width = maxX - minX;
  const height = maxZ - minZ;

  // Calculate scaling factors to fit within -10 to 10 range (20 units total)
  const xScale = width > 0 ? 20 / width : 1;
  const zScale = height > 0 ? 20 / height : 1;

  // Use the smaller scale to maintain aspect ratio
  const scale = Math.min(xScale, zScale) * 0.95; // 95% to add some margin

  // Calculate the center point of the data
  const centerX = (minX + maxX) / 2;
  const centerZ = (minZ + maxZ) / 2;

  return { 
    scale, 
    xOffset: -centerX, 
    zOffset: -centerZ 
  };
};

// Seat component - positioned relative to transformations
const Seat = React.memo(({ seat, isSelected, onClick }) => {
  const [isHovered, setIsHovered] = useState(false);
  const meshRef = useRef();

  // Generate a truly unique identifier for this seat
  const uniqueSeatId = useMemo(() => `${seat.id}-${seat.section}-${seat.row}-${seat.number}`, [seat]);

  // Use memoized material properties to prevent unnecessary recreations
  const materialProps = useMemo(() => {
    return {
      color: isSelected ? '#ff5722' : isHovered ? '#2196f3' : '#aaaaaa',
      emissive: isSelected ? '#ff9800' : isHovered ? '#bbdefb' : '#000000',
      emissiveIntensity: isSelected ? 0.5 : isHovered ? 0.3 : 0,
      metalness: 0.5,
      roughness: 0.5,
      transparent: true,
      opacity: 1.0,
      side: THREE.DoubleSide
    };
  }, [isSelected, isHovered]);

  // Handle interactions
  const handlePointerOver = useCallback(() => {
    setIsHovered(true);
    document.body.style.cursor = 'pointer';
  }, []);

  const handlePointerOut = useCallback(() => {
    setIsHovered(false);
    document.body.style.cursor = 'default';
  }, []);

  const handlePointerDown = useCallback((e) => {
    e.stopPropagation(); // Prevent event bubbling
    e.preventDefault();
    console.log(`Seat clicked: ${uniqueSeatId}`, seat);
    if (onClick) {
      onClick(seat);
    }
  }, [onClick, seat, uniqueSeatId]);

  return (
    <mesh
      ref={meshRef}
      position={[seat.coordinates.x, seat.coordinates.y, seat.coordinates.z]}
      onPointerOver={handlePointerOver}
      onPointerOut={handlePointerOut}
      onPointerDown={handlePointerDown}
    >
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial {...materialProps} />
    </mesh>
  );
});

// Storage keys for saved transformations
const STORAGE_KEYS = {
  EDIT_MODE: 'stadium_edit_mode',
  SCALE: 'stadium_scale',
  ROTATION: 'stadium_rotation',
  TRANSLATE_X: 'stadium_translate_x',
  TRANSLATE_Y: 'stadium_translate_y',
};

const StadiumMap = memo(({ seats, selectedSeat, onSeatSelect }) => {
  // Load saved values from localStorage with memoization
  const loadSavedValue = useCallback((key, defaultValue) => {
    try {
      const saved = localStorage.getItem(key);
      return saved !== null ? JSON.parse(saved) : defaultValue;
    } catch (e) {
      return defaultValue;
    }
  }, []);

  // State for transformation controls
  const [editMode, setEditMode] = useState(() => 
    loadSavedValue(STORAGE_KEYS.EDIT_MODE, true)
  );

  const [transformations, setTransformations] = useState({
    scale: loadSavedValue(STORAGE_KEYS.SCALE, 1.0),
    rotation: loadSavedValue(STORAGE_KEYS.ROTATION, 0),
    translateX: loadSavedValue(STORAGE_KEYS.TRANSLATE_X, 0),
    translateY: loadSavedValue(STORAGE_KEYS.TRANSLATE_Y, 0),
    // Initial base transformations
    baseScale: 1,
    baseOffset: { x: 0, z: 0 }
  });

  // Keep track of selected seat by index to avoid ID conflicts
  const [selectedSeatIndex, setSelectedSeatIndex] = useState(null);

  // Calculate base scaling factors based on all seat coordinates - memoize to prevent recalculation
  useEffect(() => {
    const coordinates = seats.map(seat => ({ 
      x: seat.coordinates.x, 
      z: seat.coordinates.z 
    }));

    const factors = calcScalingFactors(coordinates);

    setTransformations(prev => ({
      ...prev,
      baseScale: factors.scale,
      baseOffset: { x: factors.xOffset, z: factors.zOffset }
    }));
  }, [seats]);

  // Find the seat index from ID for highlighting - memoized for performance
  const findSeatIndexById = useCallback((seatId) => {
    if (!seatId) return null;
    return seats.findIndex(seat => String(seat.id) === String(seatId));
  }, [seats]);

  // Initialize selected seat index from props if needed
  useEffect(() => {
    if (selectedSeat && selectedSeat.id) {
      const index = findSeatIndexById(selectedSeat.id);
      if (index !== -1) {
        setSelectedSeatIndex(index);
      }
    }
  }, [selectedSeat, findSeatIndexById]);

  // Handle seat selection with optimized callback
  const handleSeatSelect = useCallback((index, viewerCoordinates, seatId) => {
    const seat = seats[index];

    // Make sure we have a valid seat
    if (!seat) {
      console.error(`No seat found at index ${index}`);
      return;
    }

    // Validate that the passed seatId matches the seat's ID
    if (String(seat.id) !== String(seatId)) {
      console.error(`Mismatch between seat IDs: expected ${seat.id}, got ${seatId}`);

      // Try to find the correct seat by ID if there's a mismatch
      const correctIndex = seats.findIndex(s => String(s.id) === String(seatId));
      if (correctIndex !== -1 && correctIndex !== index) {
        console.log(`Found correct seat at index ${correctIndex}, using that instead`);
        index = correctIndex;
        seat = seats[correctIndex];
      }
    }

    // Debug logging to help diagnose the issue
    console.log(`Selected seat ${seat.id} at index ${index} with coordinates:`, 
                viewerCoordinates, 
                `Previous selected index: ${selectedSeatIndex}`);

    // Only proceed if selecting a different seat
    if (selectedSeatIndex !== index) {
      // Update selected seat index
      setSelectedSeatIndex(index);

      // Create a new seat object with viewer-friendly coordinates for the seat viewer
      const seatWithViewerCoords = {
        ...seat,
        // Keep the original coordinates for reference
        originalCoordinates: { ...seat.coordinates },
        // Add viewer-appropriate coordinates for the seat viewer
        coordinates: viewerCoordinates
      };

      // Call parent component's selection handler with the updated seat object
      onSeatSelect(seatWithViewerCoords);
    }
  }, [seats, onSeatSelect, selectedSeatIndex]);

  // Save transformations and exit edit mode - optimized
  const saveTransformations = useCallback(() => {
    // Save to localStorage
    localStorage.setItem(STORAGE_KEYS.EDIT_MODE, JSON.stringify(false));
    localStorage.setItem(STORAGE_KEYS.SCALE, JSON.stringify(transformations.scale));
    localStorage.setItem(STORAGE_KEYS.ROTATION, JSON.stringify(transformations.rotation));
    localStorage.setItem(STORAGE_KEYS.TRANSLATE_X, JSON.stringify(transformations.translateX));
    localStorage.setItem(STORAGE_KEYS.TRANSLATE_Y, JSON.stringify(transformations.translateY));

    // Update state
    setEditMode(false);
  }, [transformations]);

  // Reset to default transformations - optimized
  const resetTransformations = useCallback(() => {
    // Reset the transformations to default values
    setTransformations(prev => ({
      ...prev,
      scale: 1.0,
      rotation: 0,
      translateX: 0,
      translateY: 0
    }));
  }, []);

  // Re-enable edit mode - optimized
  const enableEditMode = useCallback(() => {
    localStorage.setItem(STORAGE_KEYS.EDIT_MODE, JSON.stringify(true));
    setEditMode(true);
  }, []);

  // Handle transformation changes - optimized
  const handleScaleChange = useCallback((e) => {
    setTransformations(prev => ({ ...prev, scale: parseFloat(e.target.value) }));
  }, []);

  const handleRotationChange = useCallback((e) => {
    setTransformations(prev => ({ ...prev, rotation: parseInt(e.target.value) }));
  }, []);

  const handleTranslateXChange = useCallback((e) => {
    setTransformations(prev => ({ ...prev, translateX: parseFloat(e.target.value) }));
  }, []);

  const handleTranslateYChange = useCallback((e) => {
    setTransformations(prev => ({ ...prev, translateY: parseFloat(e.target.value) }));
  }, []);

  // Count seats by section for the summary - memoized to improve performance
  const sectionCounts = useMemo(() => {
    const counts = {};
    seats.forEach(seat => {
      counts[seat.section] = (counts[seat.section] || 0) + 1;
    });
    return counts;
  }, [seats]);

  // Memoize the Canvas and its contents to prevent unnecessary re-renders
  const stadiumCanvas = useMemo(() => (
    <Canvas
      camera={{ position: [0, 20, 100], fov: 15 }}
      shadows
      frameloop="demand"
      style={{
        background: 'transparent',
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: 1
      }}
      gl={{
        antialias: true,
        alpha: true,
        preserveDrawingBuffer: true
      }}
      onCreated={({ gl }) => {
        gl.setPixelRatio(window.devicePixelRatio);
        gl.setClearColor(0x000000, 0);
      }}
    >
      <ambientLight intensity={0.8} />
      <directionalLight position={[10, 50, 10]} intensity={1} castShadow />

      {/* Render seats as circles from a top-down view */}
      {seats.map((seat, index) => {
        // Create a truly unique ID for each seat combining all its properties
        const uniqueSeatId = `${seat.id}-${seat.section}-${seat.row}-${seat.number}`;

        return (
          <Seat
            key={uniqueSeatId}
            seat={seat}
            isSelected={selectedSeatIndex === index}
            onClick={handleSeatSelect}
          />
        );
      })}

      {/* Fixed camera with only zoom, no rotation */}
      <OrbitControls 
        enablePan={true}
        enableZoom={true}
        enableRotate={false}
        minDistance={5}
        maxDistance={100}
        target={[0, 0, 0]}
      />
    </Canvas>
  ), [seats, selectedSeatIndex, handleSeatSelect]);

  return (
    <MapContainer>
      <Title>Select Your Seat</Title>
      <Description>
        Click on a seat to view more details. {seats.length} seats available in {Object.keys(sectionCounts).length} sections.
      </Description>

      {!editMode && (
        <EditModeButton onClick={enableEditMode}>
          Edit Seat Positions
        </EditModeButton>
      )}

      {editMode && (
        <TransformControls>
          <ControlGroup>
            <ControlLabel>Scale:</ControlLabel>
            <RangeInput 
              type="range" 
              min="0.5" 
              max="2" 
              step="0.1" 
              value={transformations.scale}
              onChange={handleScaleChange}
            />
            <ValueDisplay>{transformations.scale.toFixed(1)}x</ValueDisplay>
          </ControlGroup>

          <ControlGroup>
            <ControlLabel>Rotation:</ControlLabel>
            <RangeInput 
              type="range" 
              min="0" 
              max="359" 
              step="1" 
              value={transformations.rotation}
              onChange={handleRotationChange}
            />
            <ValueDisplay>{transformations.rotation}¬∞</ValueDisplay>
          </ControlGroup>

          <ControlGroup>
            <ControlLabel>X Position:</ControlLabel>
            <RangeInput 
              type="range" 
              min="-10" 
              max="10" 
              step="0.5" 
              value={transformations.translateX}
              onChange={handleTranslateXChange}
            />
            <ValueDisplay>{transformations.translateX}</ValueDisplay>
          </ControlGroup>

          <ControlGroup>
            <ControlLabel>Y Position:</ControlLabel>
            <RangeInput 
              type="range" 
              min="-10" 
              max="10" 
              step="0.5" 
              value={transformations.translateY}
              onChange={handleTranslateYChange}
            />
            <ValueDisplay>{transformations.translateY}</ValueDisplay>
          </ControlGroup>

          <ButtonGroup>
            <ResetButton onClick={resetTransformations}>
              Reset
            </ResetButton>
            <SaveButton onClick={saveTransformations}>
              Save Position
            </SaveButton>
          </ButtonGroup>
        </TransformControls>
      )}

      <CanvasContainer style={{ 
        backgroundImage: `url(${process.env.PUBLIC_URL}/arena_outline.png)`,
        backgroundSize: 'contain',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundColor: '#f5f5f5'
      }}>
        {stadiumCanvas}
      </CanvasContainer>

      <Legend>
        <LegendTitle>Viewing Guide</LegendTitle>
        <LegendItem>
          <LegendColor color="#0d47a1" />
          <LegendText>Available Seat</LegendText>
        </LegendItem>
        <LegendItem>
          <LegendColor color="#2196f3" />
          <LegendText>Hovered Seat</LegendText>
        </LegendItem>
        <LegendItem>
          <LegendColor color="#f44336" />
          <LegendText>Selected Seat</LegendText>
        </LegendItem>
      </Legend>

      <ControlsHelp>
        <HelpTitle>Controls:</HelpTitle>
        <HelpText>
          <HelpItemIcon>‚öôÔ∏è</HelpItemIcon> Scroll wheel to zoom in/out
        </HelpText>
        <HelpText>
          <HelpItemIcon>üëÜ</HelpItemIcon> Right Click + Drag to pan
        </HelpText>
      </ControlsHelp>
    </MapContainer>
  );
});

