// @ts-nocheck
import React from 'react';
import styled from 'styled-components';

const SeatView = ({ seatCoordinates, onBack, seat }) => {
  if (!seat) return null;

  return (
    <ViewContainer>
      <SeatInfoContainer>
        <SeatHeader>
          <SeatTitle>Seat {seat.id} Details</SeatTitle>
        </SeatHeader>

        <SeatContent>
          <SeatDetailsCard>
            <CardTitle>Ticket Information</CardTitle>
            <DetailGrid>
              <DetailItem>
                <DetailLabel>Section</DetailLabel>
                <DetailValue>{seat.section}</DetailValue>
              </DetailItem>
              <DetailItem>
                <DetailLabel>Row</DetailLabel>
                <DetailValue>{seat.row}</DetailValue>
              </DetailItem>
              <DetailItem>
                <DetailLabel>Seat Number</DetailLabel>
                <DetailValue>{seat.number}</DetailValue>
              </DetailItem>
              <DetailItem>
                <DetailLabel>Price</DetailLabel>
                <DetailValue>${seat.price}</DetailValue>
              </DetailItem>
            </DetailGrid>
          </SeatDetailsCard>

          <SeatLocationCard>
            <CardTitle>Seat Location</CardTitle>
            <StadiumDiagram>
              <StadiumOutline>
                <Field />
                <SectionA>Section A</SectionA>
                <SectionB>Section B</SectionB>
                <SeatIndicator 
                  section={seat.section}
                  row={seat.row}
                  number={seat.number}
                />
              </StadiumOutline>
            </StadiumDiagram>
          </SeatLocationCard>

          <AdditionalInfoCard>
            <CardTitle>Additional Information</CardTitle>
            <InfoList>
              <InfoItem>
                <InfoIcon>🎟️</InfoIcon>
                <InfoText>Electronic tickets will be sent to your email</InfoText>
              </InfoItem>
              <InfoItem>
                <InfoIcon>🚪</InfoIcon>
                <InfoText>Enter through Gate {seat.section}</InfoText>
              </InfoItem>
              <InfoItem>
                <InfoIcon>🅿️</InfoIcon>
                <InfoText>Parking available in Lot {seat.section}</InfoText>
              </InfoItem>
              <InfoItem>
                <InfoIcon>🍔</InfoIcon>
                <InfoText>Concessions located near Section {seat.section}</InfoText>
              </InfoItem>
            </InfoList>
          </AdditionalInfoCard>
        </SeatContent>
      </SeatInfoContainer>

      <ViewFooter>
        <ViewNote>
          Note: This is a simplified representation of your seat location.
        </ViewNote>
      </ViewFooter>
    </ViewContainer>
  );
};

const ViewContainer = styled.div`
  height: 100%;
  display: flex;
  flex-direction: column;
  position: relative;
  background-color: #f5f7fa;
`;

const SeatInfoContainer = styled.div`
  flex: 1;
  padding: 20px;
  overflow-y: auto;
`;

const SeatHeader = styled.div`
  margin-bottom: 20px;
`;

const SeatTitle = styled.h2`
  margin: 0;
  font-size: 24px;
  font-weight: 600;
  color: #333;
`;

const SeatContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 20px;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const Card = styled.div`
  background-color: white;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
  padding: 20px;
`;

const SeatDetailsCard = styled(Card)`
  grid-column: 1;
  grid-row: 1;
`;

const SeatLocationCard = styled(Card)`
  grid-column: 2;
  grid-row: 1 / span 2;
  display: flex;
  flex-direction: column;

  @media (max-width: 768px) {
    grid-column: 1;
    grid-row: 2;
  }
`;

const AdditionalInfoCard = styled(Card)`
  grid-column: 1;
  grid-row: 2;

  @media (max-width: 768px) {
    grid-column: 1;
    grid-row: 3;
  }
`;

const CardTitle = styled.h3`
  margin: 0 0 15px 0;
  font-size: 18px;
  font-weight: 600;
  color: #333;
`;

const DetailGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 15px;
`;

const DetailItem = styled.div`
  display: flex;
  flex-direction: column;
`;

const DetailLabel = styled.span`
  font-size: 14px;
  color: #666;
  margin-bottom: 5px;
`;

const DetailValue = styled.span`
  font-size: 18px;
  font-weight: 600;
  color: #333;
`;

const StadiumDiagram = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
`;

const StadiumOutline = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  border: 2px solid #000;
  border-radius: 100px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Field = styled.div`
  width: 60%;
  height: 40%;
  background-color: #f5f5f5;
  border: 2px solid #000;
  border-radius: 50%;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 10px;
    height: 10px;
    background-color: #000;
    border-radius: 50%;
  }
`;

const SectionA = styled.div`
  position: absolute;
  top: 20%;
  left: 15%;
  font-weight: 600;
  font-size: 14px;
`;

const SectionB = styled.div`
  position: absolute;
  top: 20%;
  right: 15%;
  font-weight: 600;
  font-size: 14px;
`;

const SeatIndicator = styled.div`
  position: absolute;
  width: 16px;
  height: 16px;
  background-color: #1976d2;
  border-radius: 50%;
  top: ${props => `calc(30% + ${(props.row - 1) * 30}px)`};
  left: ${props => props.section === 'A' 
    ? `calc(25% + ${(props.number - 1) * 20}px)` 
    : `calc(65% + ${(props.number - 1) * 20}px)`};
  transform: scale(1.5);
  box-shadow: 0 0 0 4px rgba(25, 118, 210, 0.3), 0 0 0 8px rgba(25, 118, 210, 0.1);
  animation: pulse 2s infinite;

  @keyframes pulse {
    0% {
      box-shadow: 0 0 0 0 rgba(25, 118, 210, 0.5);
    }
    70% {
      box-shadow: 0 0 0 10px rgba(25, 118, 210, 0);
    }
    100% {
      box-shadow: 0 0 0 0 rgba(25, 118, 210, 0);
    }
  }
`;

const InfoList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const InfoItem = styled.li`
  display: flex;
  align-items: center;
  margin-bottom: 15px;

  &:last-child {
    margin-bottom: 0;
  }
`;

const InfoIcon = styled.span`
  font-size: 20px;
  margin-right: 15px;
`;

const InfoText = styled.span`
  font-size: 14px;
  color: #333;
`;

const ViewFooter = styled.div`
  padding: 15px 20px;
  background-color: #f9f9f9;
  border-top: 1px solid #eee;
`;

const ViewNote = styled.p`
  margin: 0;
  font-size: 14px;
  color: #666;
  text-align: center;
`;

export default SeatView;
