// @ts-nocheck
import React, { useState, useEffect, Suspense } from 'react';
import styled from 'styled-components';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, Text } from '@react-three/drei';
import { useModel } from '../utils/ModelContext';

// Top-down stadium model - now uses the loaded GLB model
const Stadium = () => {
  const { model, error, isLoading } = useModel();

  // If we have a model, show it; otherwise, show the simple version
  if (model && model.scene && !error) {
    return (
      <group>
        {/* Scale and position the stadium model for a top-down view */}
        <primitive 
          object={model.scene.clone()} 
          scale={[10, 10, 10]} 
          position={[0, -2, 0]} 
          rotation={[0, 0, 0]}
        />

        {/* Add coordinate axes for debugging */}
        <CoordinateAxes />
      </group>
    );
  }

  // Fallback simple stadium model when no 3D model is available
  return (
    <group>
      {/* Center court/field */}
      <mesh position={[0, -0.9, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[1.2, 1.2]} />
        <meshStandardMaterial color="#8bc34a" />
      </mesh>

      {/* Add coordinate axes for debugging */}
      <CoordinateAxes />
    </group>
  );
};

// Helper component to visualize coordinate system
const CoordinateAxes = () => {
  return (
    <group>
      {/* X axis - Red */}
      <mesh position={[5, 0, 0]}>
        <boxGeometry args={[10, 0.1, 0.1]} />
        <meshBasicMaterial color="red" />
      </mesh>
      <Text position={[10, 0, 0]} color="red" fontSize={1}>X</Text>

      {/* Y axis - Green */}
      <mesh position={[0, 5, 0]}>
        <boxGeometry args={[0.1, 10, 0.1]} />
        <meshBasicMaterial color="green" />
      </mesh>
      <Text position={[0, 10, 0]} color="green" fontSize={1}>Y</Text>

      {/* Z axis - Blue */}
      <mesh position={[0, 0, 5]}>
        <boxGeometry args={[0.1, 0.1, 10]} />
        <meshBasicMaterial color="blue" />
      </mesh>
      <Text position={[0, 0, 10]} color="blue" fontSize={1}>Z</Text>

      {/* Origin marker */}
      <mesh position={[0, 0, 0]}>
        <sphereGeometry args={[0.3, 16, 16]} />
        <meshBasicMaterial color="yellow" />
      </mesh>
    </group>
  );
};

// Special debug seats to help establish positioning
const DebugSeats = () => {
  // Create a set of seats at known positions to help debugging
  const debugPositions = [
    { id: "X+10", x: 10, y: 0, z: 0, color: "red" },
    { id: "X-10", x: -10, y: 0, z: 0, color: "red" },
    { id: "Z+10", x: 0, y: 0, z: 10, color: "blue" },
    { id: "Z-10", x: 0, y: 0, z: -10, color: "blue" },
    { id: "Origin", x: 0, y: 0, z: 0, color: "yellow" },
    { id: "NE", x: 10, y: 0, z: 10, color: "purple" },
    { id: "SE", x: 10, y: 0, z: -10, color: "purple" },
    { id: "SW", x: -10, y: 0, z: -10, color: "purple" },
    { id: "NW", x: -10, y: 0, z: 10, color: "purple" },
  ];

  return (
    <>
      {debugPositions.map(pos => (
        <mesh key={pos.id} position={[pos.x, 2, pos.z]}>
          <cylinderGeometry args={[0.3, 0.3, 0.3, 16]} />
          <meshBasicMaterial color={pos.color} />
          <Text position={[0, 1, 0]} color="white" fontSize={0.5} anchorX="center" anchorY="middle">
            {pos.id} ({pos.x}, {pos.z})
          </Text>
        </mesh>
      ))}
    </>
  );
};

// Find the coordinate bounds to calculate scaling factors
const calcScalingFactors = (coordinates) => {
  if (!coordinates || coordinates.length === 0) {
    return { xScale: 1, zScale: 1, xOffset: 0, zOffset: 0 };
  }

  // Find the min/max of all coordinates
  let minX = Infinity;
  let maxX = -Infinity;
  let minZ = Infinity;
  let maxZ = -Infinity;

  coordinates.forEach(coord => {
    minX = Math.min(minX, coord.x);
    maxX = Math.max(maxX, coord.x);
    minZ = Math.min(minZ, coord.z);
    maxZ = Math.max(maxZ, coord.z);
  });

  // Calculate width and height of the data
  const width = maxX - minX;
  const height = maxZ - minZ;

  // Calculate scaling factors to fit within -10 to 10 range (20 units total)
  const xScale = width > 0 ? 20 / width : 1;
  const zScale = height > 0 ? 20 / height : 1;

  // Use the smaller scale to maintain aspect ratio
  const scale = Math.min(xScale, zScale) * 0.95; // 95% to add some margin

  // Calculate the center point of the data
  const centerX = (minX + maxX) / 2;
  const centerZ = (minZ + maxZ) / 2;

  return { 
    scale, 
    xOffset: -centerX, 
    zOffset: -centerZ 
  };
};

// Seat component - positioned relative to the model and debug markers
const Seat = ({ position, isSelected, onClick, seatId, section, row, number, price, scalingFactors }) => {
  const [hovered, setHovered] = useState(false);

  // Apply scaling and centering based on the scaling factors
  const transformedPosition = [
    (position[0] + scalingFactors.xOffset) * scalingFactors.scale,
    1, // Fixed height above the model
    (position[2] + scalingFactors.zOffset) * scalingFactors.scale
  ];

  // Smaller consistent size
  const size = 0.05;

  // Dark blue color for all seats
  const baseColor = "#0d47a1"; // Dark blue
  const seatHeight = isSelected ? 0.3 : hovered ? 0.2 : 0.1;

  return (
    <group 
      position={transformedPosition}
      onClick={onClick}
      onPointerOver={() => setHovered(true)}
      onPointerOut={() => setHovered(false)}
    >
      {/* Seat cylinder */}
      <mesh>
        <cylinderGeometry args={[size, size, seatHeight, 12]} />
        <meshStandardMaterial 
          color={isSelected ? '#f44336' : hovered ? '#2196f3' : baseColor} 
          emissive={isSelected ? '#ff9e80' : hovered ? '#64b5f6' : baseColor}
          emissiveIntensity={isSelected ? 0.5 : hovered ? 0.3 : 0.1}
          metalness={0.3}
          roughness={0.7}
        />
      </mesh>

      {/* Add a ring around selected or hovered seats */}
      {(isSelected || hovered) && (
        <mesh position={[0, seatHeight/2 + 0.01, 0]}>
          <torusGeometry args={[size + 0.02, 0.005, 12, 24]} />
          <meshStandardMaterial 
            color={isSelected ? '#ff9800' : '#03a9f4'} 
            emissive={isSelected ? '#ff9800' : '#03a9f4'}
            emissiveIntensity={0.5}
          />
        </mesh>
      )}

      {/* Only show labels when hovered or selected to avoid clutter */}
      {(isSelected || hovered) && (
        <Text
          position={[0, seatHeight + 0.1, 0]}
          fontSize={0.1}
          color="#ffffff"
          anchorX="center"
          anchorY="middle"
          font={undefined}
          outlineWidth={0.02}
          outlineColor={isSelected ? '#d32f2f' : '#0288d1'}
        >
          {seatId}
        </Text>
      )}

      {/* Show detailed seat label with coordinate info */}
      {(hovered || isSelected) && (
        <Html center position={[0, seatHeight + 0.3, 0]}>
          <SeatPopup>
            <div><strong>ID:</strong> {seatId}</div>
            <div><strong>Section:</strong> {section}</div>
            <div><strong>Row:</strong> {row}</div>
            <div><strong>Seat:</strong> {number}</div>
            <div><strong>Price:</strong> ${price}</div>
            <div><strong>Raw:</strong> ({position[0].toFixed(1)}, {position[2].toFixed(1)})</div>
            <div><strong>Trans:</strong> ({transformedPosition[0].toFixed(1)}, {transformedPosition[2].toFixed(1)})</div>
          </SeatPopup>
        </Html>
      )}
    </group>
  );
};

const StadiumMap = ({ seats, selectedSeat, onSeatSelect }) => {
  // State to control debug mode
  const [showDebug, setShowDebug] = useState(true);

  // Calculate scaling factors based on all seat coordinates
  const coordinates = seats.map(seat => ({ 
    x: seat.coordinates.x, 
    z: seat.coordinates.z 
  }));

  const scalingFactors = calcScalingFactors(coordinates);

  // Count seats by section for the summary
  const [sectionCounts, setSectionCounts] = useState({});

  useEffect(() => {
    // Group seats by section
    const counts = {};
    seats.forEach(seat => {
      counts[seat.section] = (counts[seat.section] || 0) + 1;
    });
    setSectionCounts(counts);
  }, [seats]);

  return (
    <MapContainer>
      <Title>Select Your Seat</Title>
      <Description>
        Click on a seat to view more details. {seats.length} seats available in {Object.keys(sectionCounts).length} sections.
        <br />
        <small>Scale: {scalingFactors.scale.toFixed(3)}, Offsets: X={scalingFactors.xOffset.toFixed(1)}, Z={scalingFactors.zOffset.toFixed(1)}</small>
      </Description>

      <DebugControls>
        <DebugButton onClick={() => setShowDebug(!showDebug)}>
          {showDebug ? 'Hide Debug Markers' : 'Show Debug Markers'}
        </DebugButton>
      </DebugControls>

      <CanvasContainer>
        <Canvas 
          camera={{ 
            position: [0, 30, 0], 
            fov: 30, 
            up: [0, 0, -1],
            near: 1,
            far: 1000
          }}
          shadows
        >
          <ambientLight intensity={0.8} />
          <directionalLight position={[10, 50, 10]} intensity={1} castShadow />
          <directionalLight position={[-10, 50, -10]} intensity={0.5} />

          <Suspense fallback={null}>
            <Stadium />
          </Suspense>

          {/* Add debug seats for positioning reference */}
          {showDebug && <DebugSeats />}

          {/* Render seats as circles from a top-down view */}
          {seats.map((seat) => (
            <Seat
              key={seat.id}
              seatId={seat.id}
              section={seat.section}
              row={seat.row}
              number={seat.number}
              price={seat.price}
              position={[seat.coordinates.x, seat.coordinates.y, seat.coordinates.z]}
              isSelected={selectedSeat?.id === seat.id}
              onClick={() => onSeatSelect(seat)}
              scalingFactors={scalingFactors}
            />
          ))}

          {/* Fixed camera with only zoom, no rotation */}
          <OrbitControls 
            enablePan={true}
            enableZoom={true}
            enableRotate={false}
            minDistance={5}
            maxDistance={100}
            target={[0, 0, 0]}
          />
        </Canvas>
      </CanvasContainer>

      <Legend>
        <LegendTitle>Viewing Guide</LegendTitle>
        <LegendItem>
          <LegendColor color="#0d47a1" />
          <LegendText>Available Seat</LegendText>
        </LegendItem>
        <LegendItem>
          <LegendColor color="#2196f3" />
          <LegendText>Hovered Seat</LegendText>
        </LegendItem>
        <LegendItem>
          <LegendColor color="#f44336" />
          <LegendText>Selected Seat</LegendText>
        </LegendItem>
      </Legend>

      <ControlsHelp>
        <HelpTitle>Controls:</HelpTitle>
        <HelpText>
          <HelpItemIcon>‚öôÔ∏è</HelpItemIcon> Scroll wheel to zoom in/out
        </HelpText>
        <HelpText>
          <HelpItemIcon>üëÜ</HelpItemIcon> Right Click + Drag to pan
        </HelpText>
      </ControlsHelp>
    </MapContainer>
  );
};

