  background-color: #fff;
  border: 1px solid #eee;
  border-radius: 8px;
`;

const HelpTitle = styled.h3`
  font-size: 14px;
  margin: 0 0 8px 0;
  color: #333;
  font-weight: 600;
`;

const HelpText = styled.div`
  display: flex;
  align-items: center;
  font-size: 13px;
  color: #666;
  margin-bottom: 4px;
`;

const HelpItemIcon = styled.span`
  margin-right: 8px;
  font-size: 14px;
`;

export default StadiumMap;
