// @ts-nocheck
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Header from './components/Header';
import StadiumMap from './components/StadiumMap';
import SeatView from './components/SeatView';
import MiniSeatView from './components/MiniSeatView';
import TicketForm from './components/TicketForm';
import { preloadModels } from './utils/modelLoader';
import { ModelProvider } from './utils/ModelContext';

/**
 * @typedef {Object} SeatCoordinates
 * @property {number} x - X coordinate
 * @property {number} y - Y coordinate
 * @property {number} z - Z coordinate
 */

/**
 * @typedef {Object} Seat
 * @property {string} id - Seat identifier
 * @property {string} section - Section identifier
 * @property {number} row - Row number
 * @property {number} number - Seat number
 * @property {number} price - Seat price
 * @property {SeatCoordinates} coordinates - 3D coordinates for the seat view
 */

/** @type {Seat[]} */
const AVAILABLE_SEATS = [
  { 
    id: 'A1', 
    section: 'A', 
    row: 1, 
    number: 1, 
    price: 150, 
    coordinates: { x: -5, y: 2, z: 10 },
    // Optional: If you have coordinates from Blender, you can convert them
    // coordinates: convertBlenderToThreeCoordinates({ x: -5, y: -10, z: 2 })
  },
  { 
    id: 'A2', 
    section: 'A', 
    row: 1, 
    number: 2, 
    price: 150, 
    coordinates: { x: -3, y: 2, z: 10 } 
  },
  { 
    id: 'B1', 
    section: 'B', 
    row: 5, 
    number: 1, 
    price: 100, 
    coordinates: { x: 5, y: 5, z: 15 } 
  },
  { 
    id: 'B2', 
    section: 'B', 
    row: 5, 
    number: 2, 
    price: 100, 
    coordinates: { x: 7, y: 5, z: 15 } 
  },
];

// Success icon SVG component
const CheckmarkIcon = () => (
  <svg width="32" height="32" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M20 6L9 17L4 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
  </svg>
);

const App = () => {
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [viewingMode, setViewingMode] = useState(false);
  const [purchaseComplete, setPurchaseComplete] = useState(false);
  const [viewKey, setViewKey] = useState(0); // Add a key to force re-render

  // We don't need to preload models here anymore as it's handled by the ModelProvider
  // But we'll keep the modelLoaded state for compatibility
  const [modelLoaded, setModelLoaded] = useState(true);

  /**
   * Handle seat selection
   * @param {Seat} seat - The selected seat
   */
  const handleSeatSelect = (seat) => {
    setSelectedSeat(seat);
    // Reset view key when selecting a new seat
    setViewKey(prevKey => prevKey + 1);
  };

  // Toggle between map view and expanded seat view
  const handleViewToggle = () => {
    setViewingMode(prevMode => !prevMode);
    // Increment view key to force re-render of components
    setViewKey(prevKey => prevKey + 1);
  };

  // Handle ticket purchase
  const handlePurchase = () => {
    if (selectedSeat) {
      setPurchaseComplete(true);
    }
  };

  // Reset the application state
  const handleReset = () => {
    setSelectedSeat(null);
    setViewingMode(false);
    setPurchaseComplete(false);
    setViewKey(prevKey => prevKey + 1);
  };

  return (
    <ModelProvider>
      <AppContainer>
        <Header />
        <MainContent>
          {purchaseComplete ? (
            <SuccessContainer>
              <SuccessCard>
                <SuccessIcon>
                  <CheckmarkIcon />
                </SuccessIcon>
                <SuccessTitle>Thank you for your purchase!</SuccessTitle>
                <SuccessDetails>
                  <DetailRow>
                    <DetailLabel>Seat:</DetailLabel>
                    <DetailValue>{selectedSeat?.id}</DetailValue>
                  </DetailRow>
                  <DetailRow>
                    <DetailLabel>Section:</DetailLabel>
                    <DetailValue>{selectedSeat?.section}</DetailValue>
                  </DetailRow>
                  <DetailRow>
                    <DetailLabel>Row:</DetailLabel>
                    <DetailValue>{selectedSeat?.row}</DetailValue>
                  </DetailRow>
                  <DetailRow>
                    <DetailLabel>Seat Number:</DetailLabel>
                    <DetailValue>{selectedSeat?.number}</DetailValue>
                  </DetailRow>
                  <DetailRow>
                    <DetailLabel>Price:</DetailLabel>
                    <DetailValue>${selectedSeat?.price}</DetailValue>
                  </DetailRow>
                </SuccessDetails>
                <ActionButton onClick={handleReset}>Buy Another Ticket</ActionButton>
              </SuccessCard>
            </SuccessContainer>
          ) : (
            <ContentWrapper>
              <LeftPanel>
                {viewingMode ? (
                  <SeatView 
                    key={`seat-view-${viewKey}`}
                    seatCoordinates={selectedSeat?.coordinates} 
                    seat={selectedSeat}
                    onBack={handleViewToggle} 
                  />
                ) : (
                  <MapContainer>
                    <MapTitle>Select Your Seat</MapTitle>
                    <StadiumMap 
                      key={`stadium-map-${viewKey}`}
                      seats={AVAILABLE_SEATS} 
                      selectedSeat={selectedSeat} 
                      onSeatSelect={handleSeatSelect} 
                    />
                  </MapContainer>
                )}
              </LeftPanel>
              <RightPanel>
                <TicketForm 
                  key={`ticket-form-${viewKey}`}
                  selectedSeat={selectedSeat} 
                  onPurchase={handlePurchase} 
                  viewingMode={viewingMode}
                  onViewToggle={handleViewToggle}
                  modelLoaded={true}
                />
              </RightPanel>
            </ContentWrapper>
          )}
        </MainContent>
        <Footer>
          <FooterContent>
            <FooterText>Â© 2023 Stadium View. All rights reserved.</FooterText>
            <FooterLinks>
              <FooterLink>Privacy Policy</FooterLink>
