// @ts-nocheck
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, Text, GizmoHelper, GizmoViewport } from '@react-three/drei';

// Top-down stadium model
const Stadium = () => {
  return (
    <group>
      {/* Stadium floor */}
      <mesh position={[0, -1, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[2, 2]} />
        <meshStandardMaterial color="#4caf50" />
      </mesh>

      {/* Center court/field */}
      <mesh position={[0, -0.9, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[1.2, 1.2]} />
        <meshStandardMaterial color="#8bc34a" />
      </mesh>

      {/* Coordinate axis visualization for debugging */}
      <GizmoHelper alignment="bottom-right" margin={[80, 80]}>
        <GizmoViewport labelColor="white" axisColors={['red', 'green', 'blue']} />
      </GizmoHelper>

      {/* Sections */}
      <SectionLabels />
    </group>
  );
};

// Section labels
const SectionLabels = () => {
  // Map sections to their approximate positions
  const sections = [
    { id: 'A', position: [-0.3, 0, 0.5], color: '#bbdefb' },
    { id: 'B', position: [-0.3, 0, -0.5], color: '#b3e5fc' },
    { id: 'C', position: [-0.6, 0, 0], color: '#b2dfdb' },
