// @ts-nocheck
import React, { Suspense, useState, useEffect, useRef, useMemo, useCallback } from 'react';
import styled from 'styled-components';
import { Canvas, useFrame, useThree, useLoader } from '@react-three/fiber';
import { Environment, useAnimations } from '@react-three/drei';
import { positionCameraAtSeat } from '../utils/modelLoader';
import { useModel } from '../utils/ModelContext';
import * as THREE from 'three';
import { ColladaLoader } from 'three/examples/jsm/loaders/ColladaLoader';
import { getStadiumModelPath } from '../utils/modelLoader';

// Custom first-person camera controls
const FirstPersonControls = React.memo(({ seatCoordinates, mini = true }) => {
  const { camera, gl } = useThree();
  const isDragging = useRef(false);
  const lastMouseX = useRef(0);
  const lastMouseY = useRef(0);
  const rotationX = useRef(0);
  const rotationY = useRef(0);
  const lastPosition = useRef(new THREE.Vector3());

  // Set initial camera position
  useEffect(() => {
    if (camera && seatCoordinates) {
      // Position the camera exactly at the seat coordinates
      const x = seatCoordinates.x || 0;
      const y = seatCoordinates.y || 2;
      const z = seatCoordinates.z || 0;

      camera.position.set(x, y, z);
      lastPosition.current.set(x, y, z);

      // Look at the center of the stadium initially
      camera.lookAt(0, 0, 0);
    }
  }, [camera, seatCoordinates]);

  // Lock camera position on each frame - only update if needed
  useFrame(() => {
    if (camera && seatCoordinates) {
      const newX = seatCoordinates.x || 0;
      const newY = seatCoordinates.y || 2;
      const newZ = seatCoordinates.z || 0;

      // Only update position if it changed
      if (lastPosition.current.x !== newX || 
          lastPosition.current.y !== newY || 
          lastPosition.current.z !== newZ) {

        camera.position.set(newX, newY, newZ);
        lastPosition.current.set(newX, newY, newZ);
      }
    }
  });

  // Set up mouse event listeners for camera rotation
  useEffect(() => {
    if (!mini) return; // Only add listeners for mini view if needed

    const canvas = gl.domElement;

    const handleMouseDown = (e) => {
      isDragging.current = true;
      lastMouseX.current = e.clientX;
      lastMouseY.current = e.clientY;
    };

    const handleMouseUp = () => {
      isDragging.current = false;
    };

    const handleMouseMove = (e) => {
      if (!isDragging.current) return;

      // Calculate mouse movement
      const deltaX = e.clientX - lastMouseX.current;
      const deltaY = e.clientY - lastMouseY.current;
      lastMouseX.current = e.clientX;
      lastMouseY.current = e.clientY;

      // Update rotation (with sensitivity adjustment)
      const sensitivity = 0.003;
      rotationY.current -= deltaX * sensitivity;
      rotationX.current -= deltaY * sensitivity;

      // Limit vertical rotation to avoid flipping
      rotationX.current = Math.max(-Math.PI / 2, Math.min(Math.PI / 2, rotationX.current));

      // Apply rotation as quaternion
      camera.quaternion.setFromEuler(
        new THREE.Euler(rotationX.current, rotationY.current, 0, 'YXZ')
      );
    };

    // Add event listeners
    canvas.addEventListener('mousedown', handleMouseDown);
    window.addEventListener('mouseup', handleMouseUp);
    window.addEventListener('mousemove', handleMouseMove);

    // Clean up
    return () => {
      canvas.removeEventListener('mousedown', handleMouseDown);
      window.removeEventListener('mouseup', handleMouseUp);
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, [gl, mini]);

  return null;
});

// Simplified model for mini view
const SimplifiedModel = React.memo(() => {
  return useMemo(() => (
    <group>
      {/* Stadium floor */}
      <mesh position={[0, -3, 0]} rotation={[-Math.PI / 2, 0, 0]} receiveShadow={false}>
        <circleGeometry args={[150, 16]} /> {/* Reduced segments even more for mini view */}
        <meshStandardMaterial color="#4caf50" />
      </mesh>

      {/* Center court/field */}
      <mesh position={[0, -2.95, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <circleGeometry args={[50, 16]} />
        <meshStandardMaterial color="#8bc34a" />
      </mesh>

      {/* Add simple lighting */}
      <ambientLight intensity={0.7} />
      <directionalLight position={[10, 10, 5]} intensity={1} castShadow={false} />

      <fog attach="fog" args={['#e0f7fa', 100, 350]} />
    </group>
  ), []);
});

// Stadium NeRF model component
const StadiumModel = React.memo(({ seatCoordinates }) => {
  const { model, error, isLoading } = useModel();

  // Use a simplified model for better performance, especially in mini view
  if (!model || error) {
    return <SimplifiedModel />;
  }

  return (
    <group>
      {/* Add a circular platform to represent the stadium floor */}
      <mesh position={[0, -3, 0]} rotation={[-Math.PI / 2, 0, 0]} receiveShadow={false}>
        <circleGeometry args={[150, 16]} /> {/* Reduced segments */}
        <meshStandardMaterial color="#4caf50" />
      </mesh>

      {/* Basic lighting */}
      <ambientLight intensity={0.7} />
      <directionalLight position={[10, 10, 5]} intensity={1} castShadow={false} />
    </group>
  );
});

const MiniSeatView = React.memo(({ seatCoordinates, seat, mini = true }) => {
  const { isLoading } = useModel();

  // Memoize camera position to prevent unnecessary recalculations
  const cameraPosition = useMemo(() => [
    seatCoordinates?.x || 0, 
    seatCoordinates?.y || 2, 
    seatCoordinates?.z || 0
  ], [seatCoordinates?.x, seatCoordinates?.y, seatCoordinates?.z]);

  // Use a more optimized canvas configuration for the mini view
  const handleCanvasCreated = useCallback(({ gl }) => {
    gl.setPixelRatio(Math.min(window.devicePixelRatio, 1.5)); // Lower pixel ratio for mini view
    gl.setSize(gl.domElement.clientWidth, gl.domElement.clientHeight);
    gl.shadowMap.enabled = false; // Disable shadows
  }, []);

  return (
    <ViewContainer mini={mini}>
      {isLoading ? (
        <LoadingOverlay mini={mini}>
          <LoadingText mini={mini}>Loading...</LoadingText>
        </LoadingOverlay>
      ) : (
        <CanvasContainer>
          <Canvas 
            key={`mini-view-${seatCoordinates?.x}-${seatCoordinates?.y}-${seatCoordinates?.z}`}
            camera={{ 
              position: cameraPosition, 
              fov: 75 
            }}
            onCreated={handleCanvasCreated}
            frameloop="demand" // Only render when needed
            gl={{ preserveDrawingBuffer: true, antialias: false }} // Disable antialiasing for performance
          >
            <Suspense fallback={null}>
              <StadiumModel seatCoordinates={seatCoordinates} />
              <Environment preset="sunset" />
            </Suspense>
            <FirstPersonControls seatCoordinates={seatCoordinates} mini={mini} />
          </Canvas>
        </CanvasContainer>
      )}

      {mini && (
        <ViewTitle>
          Preview from Seat {seat?.id || ""}
        </ViewTitle>
      )}
    </ViewContainer>
  );
});

// Styled components
const ViewContainer = styled.div`
  position: relative;
  width: 100%;
  height: ${props => props.mini ? '150px' : '100%'};
  border-radius: ${props => props.mini ? '8px' : '0'};
  overflow: hidden;
  box-shadow: ${props => props.mini ? '0 2px 8px rgba(0,0,0,0.15)' : 'none'};
  margin-bottom: ${props => props.mini ? '15px' : '0'};
`;

const CanvasContainer = styled.div`
  width: 100%;
  height: 100%;
  background-color: #f5f5f5;
`;

const LoadingOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: rgba(255, 255, 255, 0.9);
  z-index: 10;
  font-size: ${props => props.mini ? '14px' : '18px'};
`;

const LoadingText = styled.p`
  font-size: ${props => props.mini ? '14px' : '18px'};
  font-weight: 500;
  color: #333;
`;

const ViewTitle = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 8px 12px;
  background-color: rgba(0, 0, 0, 0.5);
  color: white;
  font-size: 12px;
  font-weight: 500;
  text-align: center;
`;

export default MiniSeatView;
