// @ts-nocheck
import React, { useState, useEffect, Suspense } from 'react';
import styled from 'styled-components';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, Text, GizmoHelper, GizmoViewport } from '@react-three/drei';
import { useModel } from '../utils/ModelContext';

// Top-down stadium model - now uses the loaded GLB model
const Stadium = () => {
  const { model, error, isLoading } = useModel();

  // If we have a model, show it; otherwise, show the simple version
  if (model && model.scene && !error) {
    return (
      <group>
        {/* Scale and position the stadium model for a top-down view */}
        <primitive 
          object={model.scene.clone()} 
          scale={[10, 10, 10]} 
          position={[0, -2, 0]} 
          rotation={[0, 0, 0]}
        />

        {/* Coordinate axis visualization for debugging */}
        <GizmoHelper alignment="bottom-right" margin={[80, 80]}>
          <GizmoViewport labelColor="white" axisColors={['red', 'green', 'blue']} />
        </GizmoHelper>

        {/* Sections */}
        <SectionLabels />
      </group>
    );
  }

  // Fallback simple stadium model when no 3D model is available
  return (
    <group>
      {/* Center court/field */}
      <mesh position={[0, -0.9, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[1.2, 1.2]} />
        <meshStandardMaterial color="#8bc34a" />
      </mesh>

      {/* Coordinate axis visualization for debugging */}
      <GizmoHelper alignment="bottom-right" margin={[80, 80]}>
        <GizmoViewport labelColor="white" axisColors={['red', 'green', 'blue']} />
      </GizmoHelper>

      {/* Sections */}
      <SectionLabels />
    </group>
  );
};

// Section labels
const SectionLabels = () => {
  // Map sections to their approximate positions
  const sections = [
    { id: 'A', position: [-0.3, 0, 0.5], color: '#bbdefb' },
    { id: 'B', position: [-0.3, 0, -0.5], color: '#b3e5fc' },
    { id: 'C', position: [-0.6, 0, 0], color: '#b2dfdb' },
    { id: 'D', position: [0.3, 0, 0.5], color: '#c8e6c9' },
    { id: 'E', position: [0.3, 0, -0.5], color: '#dcedc8' },
    { id: 'F', position: [0.6, 0, 0], color: '#f0f4c3' },
    { id: 'G', position: [-0.5, 0, -0.3], color: '#e1bee7' },
    { id: 'H', position: [0, 0, -0.6], color: '#d1c4e9' },
    { id: 'J', position: [0.5, 0, -0.3], color: '#bbdefb' },
    { id: 'K', position: [-0.5, 0, 0.3], color: '#ffe0b2' },
    { id: 'L', position: [0, 0, 0.6], color: '#ffccbc' },
    { id: 'M', position: [0.5, 0, 0.3], color: '#ffcdd2' },
  ];

  return (
    <group>
      {sections.map((section) => (
        <group key={section.id} position={section.position}>
          <Html center>
            <SectionLabel style={{ backgroundColor: section.color }}>
              Section {section.id}
            </SectionLabel>
          </Html>
        </group>
      ))}
    </group>
  );
};

// Seat component - updated for better visibility in top-down view with GLB model
const Seat = ({ position, scaleMultiplier, rotationAngle, heightOffset, isSelected, onClick, seatId, section, row, number, price }) => {
  const [hovered, setHovered] = useState(false);

  // Apply rotation around Y axis (in radians)
  const rotatedX = position[0] * Math.cos(rotationAngle) - position[2] * Math.sin(rotationAngle);
  const rotatedZ = position[0] * Math.sin(rotationAngle) + position[2] * Math.cos(rotationAngle);

  // Scale the position to match the stadium model scale and apply user-defined scale
  const scaledPosition = [
    rotatedX * scaleMultiplier,  // Apply scale after rotation
    position[1] + heightOffset,  // Adjust height to be visible
    rotatedZ * scaleMultiplier   // Apply scale after rotation
  ];

  // Make the seat size much smaller but still visible from above
  const distance = Math.sqrt(scaledPosition[0]**2 + scaledPosition[2]**2);
  const size = 0.15 - Math.min(0.05, distance * 0.001);

  // Choose color based on section
  const getSectionColor = (section) => {
    const colors = {
      'A': '#ff5252', // Red
      'B': '#7c4dff', // Purple
      'C': '#40c4ff', // Light Blue
      'D': '#69f0ae', // Green
      'E': '#ffd740', // Amber
      'F': '#ff6e40', // Deep Orange
      'G': '#ec407a', // Pink
      'H': '#5c6bc0', // Indigo
      'J': '#26a69a', // Teal
      'K': '#9ccc65', // Light Green
      'L': '#ffca28', // Amber
      'M': '#78909c'  // Blue Grey
    };
    return colors[section] || '#64b5f6';
  };

  const baseColor = getSectionColor(section);
  const seatHeight = isSelected ? 0.4 : hovered ? 0.3 : 0.2;

  return (
    <group 
      // Use the scaled and rotated position
      position={scaledPosition}
      onClick={onClick}
      onPointerOver={() => setHovered(true)}
      onPointerOut={() => setHovered(false)}
    >
      {/* Make the seat a smaller cylinder */}
      <mesh>
        <cylinderGeometry args={[size, size, seatHeight, 12]} />
        <meshStandardMaterial 
          color={isSelected ? '#f44336' : hovered ? '#64b5f6' : baseColor} 
          emissive={isSelected ? '#ff9e80' : hovered ? '#80d8ff' : baseColor}
          emissiveIntensity={isSelected ? 0.5 : hovered ? 0.3 : 0.1}
          metalness={0.3}
          roughness={0.7}
        />
      </mesh>

      {/* Add a ring around selected or hovered seats */}
      {(isSelected || hovered) && (
        <mesh position={[0, seatHeight/2 + 0.02, 0]}>
          <torusGeometry args={[size + 0.03, 0.01, 12, 24]} />
          <meshStandardMaterial 
            color={isSelected ? '#ff9800' : '#03a9f4'} 
            emissive={isSelected ? '#ff9800' : '#03a9f4'}
            emissiveIntensity={0.5}
          />
        </mesh>
      )}

      {/* Show labels for all seats to see coordinates during testing */}
      {(isSelected || hovered) && (
        <Text
          position={[0, seatHeight + 0.1, 0]}
          fontSize={0.1}
          color="#ffffff"
          anchorX="center"
          anchorY="middle"
          font={undefined}
          outlineWidth={0.02}
          outlineColor={isSelected ? '#d32f2f' : '#0288d1'}
        >
          {seatId}
        </Text>
      )}

      {/* Show detailed seat label when hovered or selected */}
      {(hovered || isSelected) && (
        <Html center position={[0, seatHeight + 0.3, 0]}>
          <SeatPopup>
            <div><strong>ID:</strong> {seatId}</div>
            <div><strong>Section:</strong> {section}</div>
            <div><strong>Row:</strong> {row}</div>
            <div><strong>Seat:</strong> {number}</div>
            <div><strong>Price:</strong> ${price}</div>
            <div><strong>Coords:</strong> ({position[0].toFixed(2)}, {position[1].toFixed(2)}, {position[2].toFixed(2)})</div>
          </SeatPopup>
        </Html>
      )}
    </group>
  );
};

const StadiumMap = ({ seats, selectedSeat, onSeatSelect }) => {
  // Count seats by section for the summary
  const [sectionCounts, setSectionCounts] = useState({});

  // Controls for seat positioning
  const [scaleMultiplier, setScaleMultiplier] = useState(5); // Default scale
  const [rotationAngle, setRotationAngle] = useState(0); // In radians
  const [heightOffset, setHeightOffset] = useState(2); // Height offset

  useEffect(() => {
    // Group seats by section
    const counts = {};
    seats.forEach(seat => {
      counts[seat.section] = (counts[seat.section] || 0) + 1;
    });
    setSectionCounts(counts);
  }, [seats]);

  return (
    <MapContainer>
      <Title>Select Your Seat</Title>
      <Description>
        Click on a seat to view more details. {seats.length} seats available in {Object.keys(sectionCounts).length} sections.
      </Description>

      <SectionSummary>
        {Object.entries(sectionCounts).sort().map(([section, count]) => (
          <SectionBadge key={section}>
            {section}: {count}
          </SectionBadge>
        ))}
      </SectionSummary>

      <ControlPanel>
        <ControlGroup>
          <ControlLabel>Scale: {scaleMultiplier.toFixed(1)}</ControlLabel>
          <RangeSlider 
            type="range" 
            min="1" 
            max="20" 
            step="0.1" 
            value={scaleMultiplier} 
            onChange={(e) => setScaleMultiplier(parseFloat(e.target.value))}
          />
        </ControlGroup>

        <ControlGroup>
          <ControlLabel>Rotation: {(rotationAngle * 180 / Math.PI).toFixed(0)}°</ControlLabel>
          <RangeSlider 
            type="range" 
            min="0" 
            max={`${2 * Math.PI}`} 
            step="0.01" 
            value={rotationAngle} 
            onChange={(e) => setRotationAngle(parseFloat(e.target.value))}
          />
        </ControlGroup>

        <ControlGroup>
          <ControlLabel>Height: {heightOffset.toFixed(1)}</ControlLabel>
          <RangeSlider 
            type="range" 
            min="0" 
            max="10" 
            step="0.1" 
            value={heightOffset} 
            onChange={(e) => setHeightOffset(parseFloat(e.target.value))}
          />
        </ControlGroup>
      </ControlPanel>

      <CanvasContainer>
        <Canvas 
          camera={{ 
            position: [0, 60, 0], 
            fov: 15, 
            up: [0, 0, -1],
            near: 1,
            far: 1000
          }}
          shadows
        >
          <ambientLight intensity={0.8} />
          <directionalLight position={[10, 50, 10]} intensity={1} castShadow />
          <directionalLight position={[-10, 50, -10]} intensity={0.5} />

          <Suspense fallback={null}>
            <Stadium />
          </Suspense>

          {/* Render seats as circles from a top-down view */}
          {seats.map((seat) => (
            <Seat
              key={seat.id}
              seatId={seat.id}
              section={seat.section}
              row={seat.row}
              number={seat.number}
              price={seat.price}
              position={[seat.coordinates.x, seat.coordinates.y, seat.coordinates.z]}
              scaleMultiplier={scaleMultiplier}
              rotationAngle={rotationAngle}
              heightOffset={heightOffset}
              isSelected={selectedSeat?.id === seat.id}
              onClick={() => onSeatSelect(seat)}
            />
          ))}

          <OrbitControls 
            enablePan={true}
            enableZoom={true}
            enableRotate={true}
            minDistance={10}
            maxDistance={200}
            target={[0, 0, 0]}
            maxPolarAngle={Math.PI / 2.5} // Limit rotation to maintain top-down feel
          />
        </Canvas>
      </CanvasContainer>

      <Legend>
        <LegendTitle>Viewing Guide</LegendTitle>
        <LegendItem>
          <LegendColor color="#2196f3" />
          <LegendText>Available Seat</LegendText>
        </LegendItem>
        <LegendItem>
          <LegendColor color="#64b5f6" />
          <LegendText>Hovered Seat</LegendText>
        </LegendItem>
        <LegendItem>
          <LegendColor color="#f44336" />
          <LegendText>Selected Seat</LegendText>
        </LegendItem>
      </Legend>

      <ControlsHelp>
        <HelpTitle>Controls:</HelpTitle>
        <HelpText>
          <HelpItemIcon>🖱️</HelpItemIcon> Left Click + Drag to rotate
        </HelpText>
        <HelpText>
          <HelpItemIcon>⚙️</HelpItemIcon> Scroll wheel to zoom in/out
        </HelpText>
        <HelpText>
          <HelpItemIcon>👆</HelpItemIcon> Right Click + Drag to pan
        </HelpText>
      </ControlsHelp>
    </MapContainer>
  );
};

