// @ts-nocheck
import React, { Suspense, useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { OrbitControls, useGLTF, Environment, useAnimations } from '@react-three/drei';
import { getStadiumModelPath, positionCameraAtSeat } from '../utils/modelLoader';

// Stadium NeRF model component - same as in SeatView but with simplified controls for mini view
const StadiumModel = ({ seatCoordinates }) => {
  // Get the stadium model path
  const modelPath = getStadiumModelPath();

  // Load the model
  const { scene, animations } = useGLTF(modelPath);
  const group = useRef();
  const { actions } = useAnimations(animations, group);
  const { camera } = useThree();

  // Position the camera at the seat coordinates
  useEffect(() => {
    if (camera && seatCoordinates) {
      positionCameraAtSeat(camera, seatCoordinates);
    }
  }, [camera, seatCoordinates]);

  // Apply transformations to the model
  useEffect(() => {
    if (scene) {
      // Scale the model if needed
      scene.scale.set(1, 1, 1);

      // Position the model if needed
      scene.position.set(0, 0, 0);

      // Play any animations if they exist
      if (actions && Object.keys(actions).length > 0) {
        const actionName = Object.keys(actions)[0];
        actions[actionName]?.play();
      }
    }
  }, [scene, actions]);

  // Return the loaded model
  return (
    <group ref={group}>
      {/* Render the loaded model */}
      <primitive object={scene} />

      {/* You can add additional elements to the scene if needed */}
      <ambientLight intensity={0.5} />
      <directionalLight position={[10, 10, 5]} intensity={1} />
    </group>
  );
};

// Fallback model to use if the stadium model fails to load
const FallbackModel = () => {
  return (
    <group>
      {/* Stadium floor */}
      <mesh position={[0, -1, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[100, 100]} />
        <meshStandardMaterial color="#4caf50" />
      </mesh>

      {/* Center court/field */}
      <mesh position={[0, -0.9, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[50, 30]} />
        <meshStandardMaterial color="#8bc34a" />
      </mesh>

      {/* Players/objects on the field */}
      <mesh position={[0, 0, 0]}>
        <sphereGeometry args={[1]} />
        <meshStandardMaterial color="#f44336" />
      </mesh>
    </group>
  );
};

const MiniSeatView = ({ seatCoordinates, seat, mini = true }) => {
  const [isLoading, setIsLoading] = useState(true);
  const [modelError, setModelError] = useState(false);
  const mountedRef = useRef(true);

  // Reset state when component mounts
  useEffect(() => {
    setIsLoading(true);
    setModelError(false);

    // Simulate loading the NeRF model
    const timer = setTimeout(() => {
      if (mountedRef.current) {
        setIsLoading(false);
      }
    }, 1000); // Shorter loading time for mini view

    return () => {
      mountedRef.current = false;
      clearTimeout(timer);
    };
  }, [seatCoordinates]); // Reset when seat coordinates change

  // Handle model loading errors
  const handleModelError = () => {
    console.error("Failed to load stadium model");
    if (mountedRef.current) {
      setModelError(true);
      setIsLoading(false);
    }
  };

  return (
    <ViewContainer mini={mini}>
      {isLoading ? (
        <LoadingOverlay>
          <LoadingSpinner />
        </LoadingOverlay>
      ) : (
        <Canvas key={`mini-view-${seatCoordinates?.x}-${seatCoordinates?.y}-${seatCoordinates?.z}`} camera={{ position: [seatCoordinates?.x || 0, seatCoordinates?.y || 2, seatCoordinates?.z || 10], fov: 75 }}>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} intensity={1} />
          <Suspense fallback={null} onError={handleModelError}>
            {modelError ? (
              <FallbackModel />
            ) : (
              <StadiumModel seatCoordinates={seatCoordinates} />
            )}
            <Environment preset="sunset" />
          </Suspense>
          <OrbitControls 
            enablePan={false}
            enableZoom={mini ? false : true}
            enableRotate={true}
            minDistance={1}
            maxDistance={20}
          />
        </Canvas>
      )}

      {modelError && (
        <ErrorOverlay>
          <ErrorMessage>
            Could not load view
          </ErrorMessage>
        </ErrorOverlay>
      )}
    </ViewContainer>
  );
};

const ViewContainer = styled.div`
  height: ${props => props.mini ? '100%' : '100%'};
  width: 100%;
  position: relative;
  background-color: #f5f5f5;
`;

const LoadingOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: rgba(255, 255, 255, 0.9);
  z-index: 10;
`;

const LoadingSpinner = styled.div`
  width: 30px;
  height: 30px;
  border: 3px solid #f3f3f3;
  border-top: 3px solid #1976d2;
  border-radius: 50%;
  animation: spin 1s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const ErrorOverlay = styled.div`
  position: absolute;
  bottom: 10px;
  left: 0;
  right: 0;
  display: flex;
  justify-content: center;
  pointer-events: none;
`;

const ErrorMessage = styled.div`
  background-color: rgba(244, 67, 54, 0.8);
  color: white;
  padding: 5px 10px;
  border-radius: 4px;
  font-size: 12px;
  font-weight: 500;
`;

export default MiniSeatView;
