// @ts-nocheck
import React, { Suspense, useState, useEffect, useRef, useMemo, useCallback } from 'react';
import styled from 'styled-components';
import { Canvas, useFrame, useThree, useLoader } from '@react-three/fiber';
import { PerspectiveCamera, Environment, useAnimations } from '@react-three/drei';
import { positionCameraAtSeat } from '../utils/modelLoader';
import { useModel } from '../utils/ModelContext';
import * as THREE from 'three';
import { ColladaLoader } from 'three/examples/jsm/loaders/ColladaLoader';
import { getStadiumModelPath } from '../utils/modelLoader';

// Custom first-person camera controls
const FirstPersonControls = ({ seatCoordinates }) => {
  const { camera, gl } = useThree();
  const isDragging = useRef(false);
  const lastMouseX = useRef(0);
  const lastMouseY = useRef(0);
  const rotationX = useRef(0);
  const rotationY = useRef(0);

  // Set initial camera position
  useEffect(() => {
    if (camera && seatCoordinates) {
      // Position the camera exactly at the seat coordinates
      camera.position.set(
        seatCoordinates.x || 0,
        seatCoordinates.y || 2,
        seatCoordinates.z || 0
      );

      // Look at the center of the stadium initially
      camera.lookAt(0, 0, 0);
    }
  }, [camera, seatCoordinates]);

  // Lock camera position on each frame - but only update if position changes
  const lastPosition = useRef(new THREE.Vector3());
  useFrame(() => {
    if (camera && seatCoordinates) {
      const newX = seatCoordinates.x || 0;
      const newY = seatCoordinates.y || 2;
      const newZ = seatCoordinates.z || 0;

      // Only update position if it actually changed (performance optimization)
      if (lastPosition.current.x !== newX || 
          lastPosition.current.y !== newY || 
          lastPosition.current.z !== newZ) {

        camera.position.set(newX, newY, newZ);
        lastPosition.current.set(newX, newY, newZ);
      }
    }
  });

  // Set up mouse event listeners for camera rotation - use cleanup properly
  useEffect(() => {
    const canvas = gl.domElement;

    const handleMouseDown = (e) => {
      isDragging.current = true;
      lastMouseX.current = e.clientX;
      lastMouseY.current = e.clientY;
    };

    const handleMouseUp = () => {
      isDragging.current = false;
    };

    const handleMouseMove = (e) => {
      if (!isDragging.current) return;

      // Calculate mouse movement
      const deltaX = e.clientX - lastMouseX.current;
      const deltaY = e.clientY - lastMouseY.current;
      lastMouseX.current = e.clientX;
      lastMouseY.current = e.clientY;

      // Update rotation (with sensitivity adjustment)
      const sensitivity = 0.003;
      rotationY.current -= deltaX * sensitivity;
      rotationX.current -= deltaY * sensitivity;

      // Limit vertical rotation to avoid flipping
      rotationX.current = Math.max(-Math.PI / 2, Math.min(Math.PI / 2, rotationX.current));

      // Apply rotation as quaternion
      camera.quaternion.setFromEuler(
        new THREE.Euler(rotationX.current, rotationY.current, 0, 'YXZ')
      );
    };

    // Add event listeners
    canvas.addEventListener('mousedown', handleMouseDown);
    window.addEventListener('mouseup', handleMouseUp);
    window.addEventListener('mousemove', handleMouseMove);

    // Clean up
    return () => {
      canvas.removeEventListener('mousedown', handleMouseDown);
      window.removeEventListener('mouseup', handleMouseUp);
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, [gl]);

  return null;
};

