// Stadium NeRF model component
// This component loads the single stadium model and positions the camera at the seat location
const StadiumModel = ({ seatCoordinates }) => {
  const { model, error, isLoading } = useModel();
  const group = useRef();

  // Use a simplified model for better performance
  if (!model || error) {
    return <SimplifiedStadiumModel />;
  }

  // Apply transformations to the model only once
  useEffect(() => {
    if (model?.scene) {      
      // Scale the model appropriately - use larger scale for better visibility
      model.scene.scale.set(10, 10, 10);

      // Position the model centered at the origin
      model.scene.position.set(0, -3, 0);

      // Play any animations if they exist
      const animations = model.scene?.animations || model?.animations || [];
      if (animations.length > 0) {
        const mixer = new THREE.AnimationMixer(model.scene);
        const action = mixer.clipAction(animations[0]);
        action.play();
      }
    }
  }, [model]);

  // Return the loaded model
  return (
    <group ref={group}>
      {/* Render the loaded model */}
      {model.scene && <primitive object={model.scene} />}

      {/* Add additional elements for better visualization - reduced lights for performance */}
      <ambientLight intensity={0.7} />
      <directionalLight position={[10, 10, 5]} intensity={1} castShadow={false} />

      {/* Add a circular platform to represent the stadium floor */}
      <mesh position={[0, -3, 0]} rotation={[-Math.PI / 2, 0, 0]} receiveShadow={false}>
        <circleGeometry args={[150, 32]} /> {/* Reduced segments */}
        <meshStandardMaterial color="#4caf50" />
      </mesh>
    </group>
  );
};

