  // Memoize camera position to prevent unnecessary recalculations
  const cameraPosition = useMemo(() => [
    seatCoordinates?.x || 0, 
    seatCoordinates?.y || 2, 
    seatCoordinates?.z || 0
  ], [seatCoordinates?.x, seatCoordinates?.y, seatCoordinates?.z]);

  return (
    <ViewContainer>
