        <LoadingOverlay>
          <LoadingText>Loading 3D View...</LoadingText>
          <LoadingSpinner />
        </LoadingOverlay>
      ) : (
        <CanvasContainer>
          <Canvas 
            key={`seat-view-${seatCoordinates?.x}-${seatCoordinates?.y}-${seatCoordinates?.z}`}
            camera={{ 
              position: cameraPosition, 
              fov: 75 
            }}
            onCreated={({ gl }) => {
              // Optimize renderer settings
              gl.domElement.style.touchAction = 'none';
              gl.setPixelRatio(Math.min(window.devicePixelRatio, 2)); // Limit pixel ratio for performance
              gl.setSize(gl.domElement.clientWidth, gl.domElement.clientHeight);
              gl.shadowMap.enabled = false; // Disable shadows for performance
            }}
            frameloop="demand" // Only render when needed for better performance
          >
            <ambientLight intensity={0.5} />
            <pointLight position={[10, 10, 10]} intensity={1} />
            <Suspense fallback={null}>
              <StadiumModel seatCoordinates={seatCoordinates} />
              <Environment preset="sunset" />
            </Suspense>
            <FirstPersonControls seatCoordinates={seatCoordinates} />
          </Canvas>

          {error && (
            <ErrorOverlay>
              <ErrorMessage>
                Could not load the stadium model. Showing a simplified view instead.
              </ErrorMessage>
            </ErrorOverlay>
          )}
        </CanvasContainer>
