// @ts-nocheck
import React, { useState } from 'react';
import styled from 'styled-components';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html } from '@react-three/drei';

// Top-down stadium model
const Stadium = () => {
  return (
    <group>
      {/* Stadium floor */}
      <mesh position={[0, -1, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[30, 30]} />
        <meshStandardMaterial color="#4caf50" />
      </mesh>

      {/* Center court/field */}
      <mesh position={[0, -0.9, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[20, 10]} />
        <meshStandardMaterial color="#8bc34a" />
      </mesh>

      {/* Section A - Left side */}
      <mesh position={[-10, -0.5, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[8, 20]} />
        <meshStandardMaterial color="#bbdefb" />
        <Html position={[0, 0.1, 0]} center>
          <SectionLabel>Section A</SectionLabel>
        </Html>
      </mesh>

      {/* Section B - Right side */}
      <mesh position={[10, -0.5, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[8, 20]} />
        <meshStandardMaterial color="#bbdefb" />
        <Html position={[0, 0.1, 0]} center>
          <SectionLabel>Section B</SectionLabel>
        </Html>
      </mesh>
    </group>
  );
};

// Seat component - now a circle viewed from above
const Seat = ({ position, isSelected, onClick, seatId }) => {
  const [hovered, setHovered] = useState(false);

  return (
    <group 
      position={[position[0], 0.1, position[2]]} 
      onClick={onClick}
      onPointerOver={() => setHovered(true)}
      onPointerOut={() => setHovered(false)}
    >
      <mesh rotation={[-Math.PI / 2, 0, 0]}>
        <circleGeometry args={[0.7, 32]} />
        <meshStandardMaterial color={isSelected ? '#f44336' : hovered ? '#64b5f6' : '#2196f3'} />
      </mesh>
    </group>
  );
};

const StadiumMap = ({ seats, selectedSeat, onSeatSelect }) => {
  return (
    <MapContainer>
      <Title>Select Your Seat</Title>
      <Description>
        Click on a seat to select it. Seat details will appear after selection.
      </Description>
      <CanvasContainer>
        <Canvas camera={{ position: [0, 25, 0], fov: 50, up: [0, 0, -1] }}>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} intensity={1} />
          <Stadium />

          {/* Render seats as circles from a top-down view */}
          {seats.map((seat) => (
            <Seat
              key={seat.id}
              seatId={seat.id}
              position={[seat.coordinates.x, seat.coordinates.y, seat.coordinates.z]}
              isSelected={selectedSeat?.id === seat.id}
              onClick={() => onSeatSelect(seat)}
            />
          ))}

          <OrbitControls 
            enablePan={true}
            enableZoom={true}
            enableRotate={false}
            minDistance={10}
            maxDistance={50}
          />
        </Canvas>
      </CanvasContainer>
      <Legend>
        <LegendTitle>Seat Legend</LegendTitle>
        <LegendItem>
          <LegendColor color="#2196f3" />
          <LegendText>Available Seat</LegendText>
        </LegendItem>
        <LegendItem>
          <LegendColor color="#64b5f6" />
          <LegendText>Hovered Seat</LegendText>
        </LegendItem>
        <LegendItem>
          <LegendColor color="#f44336" />
          <LegendText>Selected Seat</LegendText>
        </LegendItem>
      </Legend>
    </MapContainer>
  );
};

const SectionLabel = styled.div`
  background-color: rgba(25, 118, 210, 0.8);
  color: white;
  padding: 4px 8px;
  border-radius: 4px;
  font-weight: bold;
  font-size: 12px;
  white-space: nowrap;
  pointer-events: none;
`;

const SeatLabel = styled.div`
  background-color: rgba(0, 0, 0, 0.7);
  color: white;
  padding: 2px 4px;
  border-radius: 2px;
  font-size: 10px;
  white-space: nowrap;
  pointer-events: none;
`;

const MapContainer = styled.div`
  height: 100%;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const Title = styled.h2`
  margin-bottom: 10px;
  color: #333;
  font-size: 24px;
  font-weight: 600;
  position: relative;
  padding-bottom: 8px;

  &:after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 50px;
    height: 3px;
    background-color: #1976d2;
    border-radius: 3px;
  }
`;

const Description = styled.p`
  margin-bottom: 20px;
  color: #666;
  font-size: 16px;
  line-height: 1.5;
  max-width: 600px;
`;

const CanvasContainer = styled.div`
  flex: 1;
  border-radius: 8px;
  overflow: hidden;
  background-color: #f5f5f5;
`;

const Legend = styled.div`
  margin-top: 20px;
  padding: 15px;
  background-color: #f5f5f5;
  border-radius: 8px;
  display: flex;
  flex-wrap: wrap;
  gap: 15px;
  align-items: center;
`;

const LegendTitle = styled.div`
  font-weight: 600;
  margin-right: 10px;
  color: #333;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;
  margin-right: 15px;
`;

const LegendColor = styled.div`
  width: 16px;
  height: 16px;
  border-radius: 50%;
  background-color: ${props => props.color};
  margin-right: 8px;
  border: 1px solid rgba(0, 0, 0, 0.1);
`;

const LegendText = styled.span`
  font-size: 14px;
  color: #555;
`;

export default StadiumMap;
