// @ts-nocheck
import React, { useState, useEffect, Suspense } from 'react';
import styled from 'styled-components';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, Text, GizmoHelper, GizmoViewport } from '@react-three/drei';
import { useModel } from '../utils/ModelContext';

// Top-down stadium model - now uses the loaded GLB model
const Stadium = () => {
  const { model, error, isLoading } = useModel();

  // If we have a model, show it; otherwise, show the simple version
  if (model && model.scene && !error) {
    return (
      <group>
        {/* Scale and position the stadium model for a top-down view */}
        <primitive 
          object={model.scene.clone()} 
          scale={[5, 5, 5]} 
          position={[0, 0, 0]} 
          rotation={[0, 0, 0]}
        />

        {/* Keep the floor as a backup */}
        <mesh position={[0, -0.1, 0]} rotation={[-Math.PI / 2, 0, 0]} receiveShadow>
          <circleGeometry args={[150, 64]} />
          <meshStandardMaterial color="#4caf50" transparent opacity={0.1} />
        </mesh>

        {/* Coordinate axis visualization for debugging */}
        <GizmoHelper alignment="bottom-right" margin={[80, 80]}>
          <GizmoViewport labelColor="white" axisColors={['red', 'green', 'blue']} />
        </GizmoHelper>

        {/* Sections */}
        <SectionLabels />
      </group>
    );
  }

  // Fallback simple stadium model when no 3D model is available
  return (
    <group>
      {/* Stadium floor */}
      <mesh position={[0, -1, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[2, 2]} />
        <meshStandardMaterial color="#4caf50" />
      </mesh>

      {/* Center court/field */}
      <mesh position={[0, -0.9, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[1.2, 1.2]} />
        <meshStandardMaterial color="#8bc34a" />
      </mesh>

      {/* Coordinate axis visualization for debugging */}
      <GizmoHelper alignment="bottom-right" margin={[80, 80]}>
        <GizmoViewport labelColor="white" axisColors={['red', 'green', 'blue']} />
      </GizmoHelper>

      {/* Sections */}
      <SectionLabels />
    </group>
  );
};

// Section labels
const SectionLabels = () => {
  // Map sections to their approximate positions
  const sections = [
    { id: 'A', position: [-0.3, 0, 0.5], color: '#bbdefb' },
    { id: 'B', position: [-0.3, 0, -0.5], color: '#b3e5fc' },
    { id: 'C', position: [-0.6, 0, 0], color: '#b2dfdb' },
    { id: 'D', position: [0.3, 0, 0.5], color: '#c8e6c9' },
    { id: 'E', position: [0.3, 0, -0.5], color: '#dcedc8' },
    { id: 'F', position: [0.6, 0, 0], color: '#f0f4c3' },
    { id: 'G', position: [-0.5, 0, -0.3], color: '#e1bee7' },
    { id: 'H', position: [0, 0, -0.6], color: '#d1c4e9' },
    { id: 'J', position: [0.5, 0, -0.3], color: '#bbdefb' },
    { id: 'K', position: [-0.5, 0, 0.3], color: '#ffe0b2' },
    { id: 'L', position: [0, 0, 0.6], color: '#ffccbc' },
    { id: 'M', position: [0.5, 0, 0.3], color: '#ffcdd2' },
  ];

  return (
    <group>
      {sections.map((section) => (
        <group key={section.id} position={section.position}>
          <Html center>
            <SectionLabel style={{ backgroundColor: section.color }}>
              Section {section.id}
            </SectionLabel>
          </Html>
        </group>
      ))}
    </group>
  );
};

// Seat component - updated for better visibility in top-down view with GLB model
const Seat = ({ position, isSelected, onClick, seatId, section, row, number, price }) => {
  const [hovered, setHovered] = useState(false);

  // Make the seat size larger for better visibility from above
  const distance = Math.sqrt(position[0]**2 + position[2]**2);
  const size = 1.0 - Math.min(0.5, distance * 0.02);

  // Choose color based on section
  const getSectionColor = (section) => {
    const colors = {
      'A': '#bbdefb', 'B': '#b3e5fc', 'C': '#b2dfdb',
      'D': '#c8e6c9', 'E': '#dcedc8', 'F': '#f0f4c3',
      'G': '#e1bee7', 'H': '#d1c4e9', 'J': '#bbdefb',
      'K': '#ffe0b2', 'L': '#ffccbc', 'M': '#ffcdd2'
    };
    return colors[section] || '#64b5f6';
  };

  const baseColor = getSectionColor(section);
  const seatHeight = isSelected ? 1.5 : hovered ? 1.2 : 1.0;

  return (
    <group 
      // Position the seat at the correct coordinates, but raise it slightly above the model
      position={[position[0], 5, position[2]]} 
      onClick={onClick}
      onPointerOver={() => setHovered(true)}
      onPointerOut={() => setHovered(false)}
    >
      {/* Make the seat a taller cylinder for better visibility */}
      <mesh>
        <cylinderGeometry args={[size, size, seatHeight, 16]} />
        <meshStandardMaterial color={isSelected ? '#f44336' : hovered ? '#64b5f6' : baseColor} />
      </mesh>

      {/* Add a ring around selected or hovered seats */}
      {(isSelected || hovered) && (
        <mesh position={[0, seatHeight/2 + 0.05, 0]}>
          <torusGeometry args={[size + 0.2, 0.1, 16, 32]} />
          <meshStandardMaterial color={isSelected ? '#ff9800' : '#03a9f4'} />
        </mesh>
      )}

      {/* Label above the seat to show its ID regardless of view angle */}
      <Text
        position={[0, seatHeight + 0.3, 0]}
        fontSize={0.3}
        color={isSelected ? '#f44336' : hovered ? '#2196f3' : '#000000'}
        anchorX="center"
        anchorY="middle"
        font={undefined}
        outlineWidth={0.01}
        outlineColor="#ffffff"
      >
        {seatId}
      </Text>

      {/* Show detailed seat label when hovered or selected */}
      {(hovered || isSelected) && (
        <Html center position={[0, seatHeight + 1, 0]}>
          <SeatPopup>
            <div><strong>ID:</strong> {seatId}</div>
            <div><strong>Section:</strong> {section}</div>
            <div><strong>Row:</strong> {row}</div>
            <div><strong>Seat:</strong> {number}</div>
            <div><strong>Price:</strong> ${price}</div>
          </SeatPopup>
        </Html>
      )}
    </group>
  );
};

const StadiumMap = ({ seats, selectedSeat, onSeatSelect }) => {
  // Count seats by section for the summary
  const [sectionCounts, setSectionCounts] = useState({});

  useEffect(() => {
    // Group seats by section
    const counts = {};
    seats.forEach(seat => {
      counts[seat.section] = (counts[seat.section] || 0) + 1;
    });
    setSectionCounts(counts);
  }, [seats]);

  return (
    <MapContainer>
      <Title>Select Your Seat</Title>
      <Description>
        Click on a seat to view more details. {seats.length} seats available in {Object.keys(sectionCounts).length} sections.
      </Description>

      <SectionSummary>
        {Object.entries(sectionCounts).sort().map(([section, count]) => (
          <SectionBadge key={section}>
            {section}: {count}
          </SectionBadge>
        ))}
      </SectionSummary>

      <CanvasContainer>
        <Canvas 
          camera={{ 
            position: [0, 40, 0], 
            fov: 20, 
            up: [0, 0, -1],
            near: 1,
            far: 1000
          }}
          shadows
        >
          <ambientLight intensity={0.8} />
          <directionalLight position={[10, 50, 10]} intensity={1} castShadow />
          <directionalLight position={[-10, 50, -10]} intensity={0.5} />

          <Suspense fallback={null}>
            <Stadium />
          </Suspense>

          {/* Render seats as circles from a top-down view */}
          {seats.map((seat) => (
            <Seat
              key={seat.id}
              seatId={seat.id}
              section={seat.section}
              row={seat.row}
              number={seat.number}
              price={seat.price}
              position={[seat.coordinates.x, seat.coordinates.y, seat.coordinates.z]}
              isSelected={selectedSeat?.id === seat.id}
              onClick={() => onSeatSelect(seat)}
            />
          ))}

          <OrbitControls 
            enablePan={true}
            enableZoom={true}
            enableRotate={true}
            minDistance={10}
            maxDistance={200}
            target={[0, 0, 0]}
            maxPolarAngle={Math.PI / 2.5} // Limit rotation to maintain top-down feel
          />
        </Canvas>
      </CanvasContainer>

      <Legend>
        <LegendTitle>Viewing Guide</LegendTitle>
        <LegendItem>
          <LegendColor color="#2196f3" />
          <LegendText>Available Seat</LegendText>
        </LegendItem>
        <LegendItem>
          <LegendColor color="#64b5f6" />
          <LegendText>Hovered Seat</LegendText>
        </LegendItem>
        <LegendItem>
          <LegendColor color="#f44336" />
          <LegendText>Selected Seat</LegendText>
        </LegendItem>
      </Legend>

      <ControlsHelp>
        <HelpTitle>Controls:</HelpTitle>
        <HelpText>
          <HelpItemIcon>üñ±Ô∏è</HelpItemIcon> Left Click + Drag to rotate
        </HelpText>
        <HelpText>
          <HelpItemIcon>‚öôÔ∏è</HelpItemIcon> Scroll wheel to zoom in/out
        </HelpText>
        <HelpText>
          <HelpItemIcon>üëÜ</HelpItemIcon> Right Click + Drag to pan
        </HelpText>
      </ControlsHelp>
    </MapContainer>
  );
};

const SectionLabel = styled.div`
  background-color: rgba(25, 118, 210, 0.8);
  color: white;
  padding: 4px 8px;
  border-radius: 4px;
  font-weight: bold;
  font-size: 12px;
  white-space: nowrap;
  pointer-events: none;
`;

const SeatPopup = styled.div`
  background-color: rgba(0, 0, 0, 0.7);
  color: white;
  padding: 6px 8px;
  border-radius: 4px;
  font-size: 11px;
  min-width: 120px;
  white-space: nowrap;
  pointer-events: none;
  transform: translateY(-100%);
  margin-bottom: 5px;

  div {
    margin-bottom: 2px;
  }

  strong {
    margin-right: 4px;
  }
`;

const MapContainer = styled.div`
  height: 100%;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const Title = styled.h2`
  margin-bottom: 10px;
  color: #333;
  font-size: 24px;
  font-weight: 600;
  position: relative;
  padding-bottom: 8px;

  &:after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 50px;
    height: 3px;
    background-color: #1976d2;
    border-radius: 3px;
  }
`;

const Description = styled.p`
  margin-bottom: 12px;
  color: #666;
  font-size: 15px;
  line-height: 1.5;
  max-width: 600px;
`;

const SectionSummary = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 6px;
  margin-bottom: 15px;
`;

const SectionBadge = styled.div`
  background-color: #e3f2fd;
  border: 1px solid #bbdefb;
  color: #1976d2;
  padding: 4px 8px;
  border-radius: 12px;
  font-size: 12px;
  font-weight: 600;
`;

const CanvasContainer = styled.div`
  flex: 1;
  border-radius: 8px;
  overflow: hidden;
  background-color: #f5f5f5;
`;

const Legend = styled.div`
  margin-top: 15px;
  padding: 12px;
  background-color: #f5f5f5;
  border-radius: 8px;
  display: flex;
  flex-wrap: wrap;
  gap: 15px;
  align-items: center;
`;

const LegendTitle = styled.div`
  font-weight: 600;
  margin-right: 10px;
  color: #333;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;
  margin-right: 15px;
`;

const LegendColor = styled.div`
  width: 16px;
  height: 16px;
  border-radius: 50%;
  background-color: ${props => props.color};
  margin-right: 8px;
  border: 1px solid rgba(0, 0, 0, 0.1);
`;

const LegendText = styled.span`
  font-size: 14px;
  color: #555;
`;

const ControlsHelp = styled.div`
  margin-top: 10px;
  padding: 12px;
  background-color: #fff;
  border: 1px solid #eee;
  border-radius: 8px;
`;

const HelpTitle = styled.h3`
  font-size: 14px;
  margin: 0 0 8px 0;
  color: #333;
  font-weight: 600;
`;

const HelpText = styled.div`
  display: flex;
  align-items: center;
  font-size: 13px;
  color: #666;
  margin-bottom: 4px;
`;

const HelpItemIcon = styled.span`
  margin-right: 8px;
  font-size: 14px;
`;

export default StadiumMap;
