// @ts-nocheck
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import MiniSeatView from './MiniSeatView';

const TicketForm = ({ selectedSeat, onViewToggle, onPurchase, viewingMode }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    paymentMethod: 'credit'
  });

  const [errors, setErrors] = useState({});
  const [expandView, setExpandView] = useState(false);

  // Reset expandView state when viewingMode changes
  useEffect(() => {
    if (!viewingMode) {
      setExpandView(false);
    }
  }, [viewingMode]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when field is edited
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Phone number is required';
    } else if (!/^\d{10}$/.test(formData.phone.replace(/\D/g, ''))) {
      newErrors.phone = 'Phone number is invalid';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      onPurchase();
    }
  };

  const toggleExpandView = () => {
    if (selectedSeat) {
      setExpandView(!expandView);
    }
  };

  const handleViewToggleClick = () => {
    if (selectedSeat) {
      onViewToggle();
    }
  };

  return (
    <FormContainer>
      {viewingMode ? (
        <ViewingModeMessage>
          <ViewingModeIcon>üëÅÔ∏è</ViewingModeIcon>
          <ViewingModeText>You are currently in viewing mode</ViewingModeText>
          <BackButton onClick={handleViewToggleClick}>
            Back to Seat Selection
          </BackButton>
        </ViewingModeMessage>
      ) : (
        <>
          <FormHeader>
            <FormTitle>Purchase Ticket</FormTitle>
            {selectedSeat && (
              <SeatBadge>
                Seat {selectedSeat.id} - ${selectedSeat.price}
              </SeatBadge>
            )}
          </FormHeader>

          {selectedSeat ? (
            <>
              <SeatViewContainer expanded={expandView}>
                <MiniSeatView 
                  seatCoordinates={selectedSeat.coordinates} 
                  seat={selectedSeat} 
                  mini={!expandView}
                />
                <ViewControls>
                  <ExpandButton onClick={toggleExpandView}>
                    {expandView ? 'Collapse View' : 'Expand View'}
                  </ExpandButton>
                  <FullViewButton onClick={handleViewToggleClick}>
                    Full Screen View
                  </FullViewButton>
                </ViewControls>
              </SeatViewContainer>

              <SeatInfo>
                <SeatInfoItem>
                  <SeatInfoLabel>Section</SeatInfoLabel>
                  <SeatInfoValue>{selectedSeat.section}</SeatInfoValue>
                </SeatInfoItem>
                <SeatInfoItem>
                  <SeatInfoLabel>Row</SeatInfoLabel>
                  <SeatInfoValue>{selectedSeat.row}</SeatInfoValue>
                </SeatInfoItem>
                <SeatInfoItem>
                  <SeatInfoLabel>Seat</SeatInfoLabel>
                  <SeatInfoValue>{selectedSeat.number}</SeatInfoValue>
                </SeatInfoItem>
                <SeatInfoItem>
                  <SeatInfoLabel>Price</SeatInfoLabel>
                  <SeatInfoValue>${selectedSeat.price}</SeatInfoValue>
                </SeatInfoItem>
              </SeatInfo>

              <Form onSubmit={handleSubmit}>
                <FormGroup>
                  <FormLabel htmlFor="name">Full Name</FormLabel>
                  <FormInput
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="Enter your full name"
                    hasError={!!errors.name}
                  />
                  {errors.name && <ErrorMessage>{errors.name}</ErrorMessage>}
                </FormGroup>

                <FormGroup>
                  <FormLabel htmlFor="email">Email Address</FormLabel>
                  <FormInput
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Enter your email address"
                    hasError={!!errors.email}
                  />
                  {errors.email && <ErrorMessage>{errors.email}</ErrorMessage>}
                </FormGroup>

                <FormGroup>
                  <FormLabel htmlFor="phone">Phone Number</FormLabel>
                  <FormInput
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    placeholder="Enter your phone number"
                    hasError={!!errors.phone}
                  />
                  {errors.phone && <ErrorMessage>{errors.phone}</ErrorMessage>}
                </FormGroup>

                <FormGroup>
                  <FormLabel>Payment Method</FormLabel>
                  <PaymentOptions>
                    <PaymentOption>
                      <RadioInput
                        type="radio"
                        id="credit"
                        name="paymentMethod"
                        value="credit"
                        checked={formData.paymentMethod === 'credit'}
                        onChange={handleChange}
                      />
                      <RadioLabel htmlFor="credit">
                        <CreditCardIcon>üí≥</CreditCardIcon>
                        Credit Card
                      </RadioLabel>
                    </PaymentOption>

                    <PaymentOption>
                      <RadioInput
                        type="radio"
                        id="paypal"
                        name="paymentMethod"
                        value="paypal"
                        checked={formData.paymentMethod === 'paypal'}
                        onChange={handleChange}
                      />
                      <RadioLabel htmlFor="paypal">
                        <PayPalIcon>üÖøÔ∏è</PayPalIcon>
                        PayPal
                      </RadioLabel>
                    </PaymentOption>
                  </PaymentOptions>
                </FormGroup>

                <SubmitButton type="submit">
                  Purchase Ticket - ${selectedSeat.price}
                </SubmitButton>
              </Form>
            </>
          ) : (
            <NoSeatSelected>
              <NoSeatIcon>üéüÔ∏è</NoSeatIcon>
              <NoSeatText>Please select a seat from the stadium map to continue</NoSeatText>
            </NoSeatSelected>
          )}
        </>
      )}
    </FormContainer>
  );
};

