// @ts-nocheck
import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Html, Text } from '@react-three/drei';

// Top-down stadium model with improved visuals
const Stadium = () => {
  return (
    <group>
      {/* Stadium outer area */}
      <mesh position={[0, -1.1, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[40, 40]} />
        <meshStandardMaterial color="#e0e0e0" />
      </mesh>

      {/* Stadium floor */}
      <mesh position={[0, -1, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[30, 30]} />
        <meshStandardMaterial color="#4caf50" />
      </mesh>

      {/* Center court/field */}
      <mesh position={[0, -0.9, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeGeometry args={[20, 10]} />
        <meshStandardMaterial color="#8bc34a" />
      </mesh>

      {/* Court markings */}
      <mesh position={[0, -0.85, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <ringGeometry args={[4, 4.1, 32]} />
        <meshStandardMaterial color="#ffffff" />
      </mesh>

      {/* Section A - Left side */}
      <group position={[-10, 0, 0]}>
        <mesh position={[0, -0.5, 0]} rotation={[-Math.PI / 2, 0, 0]}>
          <planeGeometry args={[8, 20]} />
          <meshStandardMaterial color="#bbdefb" />
        </mesh>
        <Text
          position={[0, 0.5, 0]}
          rotation={[-Math.PI / 2, 0, 0]}
          fontSize={1.5}
          color="#1565c0"
          anchorX="center"
          anchorY="middle"
        >
          Section A
        </Text>
      </group>

      {/* Section B - Right side */}
      <group position={[10, 0, 0]}>
        <mesh position={[0, -0.5, 0]} rotation={[-Math.PI / 2, 0, 0]}>
          <planeGeometry args={[8, 20]} />
          <meshStandardMaterial color="#bbdefb" />
        </mesh>
        <Text
          position={[0, 0.5, 0]}
          rotation={[-Math.PI / 2, 0, 0]}
          fontSize={1.5}
          color="#1565c0"
          anchorX="center"
          anchorY="middle"
        >
          Section B
        </Text>
      </group>

      {/* Stadium walls */}
      <mesh position={[0, 0, -15]} rotation={[0, 0, 0]}>
        <boxGeometry args={[30, 2, 0.5]} />
        <meshStandardMaterial color="#e0e0e0" />
      </mesh>

      <mesh position={[0, 0, 15]} rotation={[0, 0, 0]}>
        <boxGeometry args={[30, 2, 0.5]} />
        <meshStandardMaterial color="#e0e0e0" />
      </mesh>

      <mesh position={[-15, 0, 0]} rotation={[0, Math.PI / 2, 0]}>
        <boxGeometry args={[30, 2, 0.5]} />
        <meshStandardMaterial color="#e0e0e0" />
      </mesh>

      <mesh position={[15, 0, 0]} rotation={[0, Math.PI / 2, 0]}>
        <boxGeometry args={[30, 2, 0.5]} />
        <meshStandardMaterial color="#e0e0e0" />
      </mesh>
    </group>
  );
};

// Animated Seat component
const Seat = ({ position, isSelected, onClick, seatId }) => {
  const [hovered, setHovered] = useState(false);
  const meshRef = useRef();

  // Animate the seat when selected or hovered
  useFrame(() => {
    if (meshRef.current) {
      if (isSelected) {
        meshRef.current.scale.y = Math.max(1, meshRef.current.scale.y * 0.95 + 1.5 * 0.05);
        meshRef.current.position.y = 0.3;
      } else if (hovered) {
        meshRef.current.scale.y = Math.max(1, meshRef.current.scale.y * 0.9 + 1.2 * 0.1);
        meshRef.current.position.y = 0.2;
      } else {
        meshRef.current.scale.y = Math.max(1, meshRef.current.scale.y * 0.9 + 1.0 * 0.1);
        meshRef.current.position.y = 0.1;
      }
    }
  });

  return (
    <group 
      position={[position[0], 0, position[2]]} 
      onClick={onClick}
      onPointerOver={() => setHovered(true)}
      onPointerOut={() => setHovered(false)}
    >
      <mesh ref={meshRef} position={[0, 0.1, 0]}>
        <cylinderGeometry args={[0.4, 0.4, 0.2, 32]} />
        <meshStandardMaterial 
          color={isSelected ? '#f44336' : hovered ? '#ff9800' : '#2196f3'} 
          emissive={isSelected ? '#f44336' : hovered ? '#ff9800' : '#000000'}
          emissiveIntensity={isSelected ? 0.5 : hovered ? 0.3 : 0}
          metalness={0.5}
          roughness={0.2}
